style.css:
@-moz-document url-prefix("http://craftsite.pl/forum/") {
li#nav_explore.right>a {
width: 250px;
text-align: center;
}
#header_bar {
background: url("http://u.cubeupload.com/Mursztin/fmenuleft.png") no-repeat top left, url("http://u.cubeupload.com/Mursztin/fmenuright.png") no-repeat top right, url("http://u.cubeupload.com/Mursztin/forumtlo.png") repeat !important;
box-shadow: 2px 4px 7px -4px #000000;
position: fixed;
z-index: 999999;
border-radius: 0 0 5px 5px;
right: 123px;
min-width: 20px;
width: 660px;
padding: 0 0 0 24px;
top: 0;
}
#user_navigation {
right: 20px;
position: absolute;
}
html, body {
top: 25px;
}
#user_navigation a {
text-shadow: 1px 2px 1px #303030;;
color: #fff;
font-size: 12px;
}
#admin_bar a {
text-shadow: 1px 2px 1px #303030;;
color: #FFFFFF;
font-size: 12px;
}
#user_link.menu_active, #notify_link.menu_active, #inbox_link.menu_active {
text-shadow: none;
}
div#user_inbox_link_menucontent {
z-index: 999999 !important;
}
div#user_notifications_link_menucontent {
z-index: 999999 !important;
}
#user_navigation a {
top: 0px;
}
}
