style.css:
@-moz-document regexp("https://www.tagesanzeiger.ch/.*/story/.*")
{
    #topNav,
    #tagiLogo,
    articleLogo,
    div.socialLinks,
    div#feedbackBox,
    div#rateBox,
    div#mainColRight,
    div#mainColBottom,
    div#mainColBottomDiashow,
    div#wideBoardContainer,
    div.ad-outside-right,
    div.relatedThema,
    div#mainColBottomAd,
    ul#bottomNav,
    div#footerLinks,
    div.article-oberzeile,
    /*
    div#mainColBottomMore,
    div#wideBoard,
    */
    div#overlay_adblock,
    div.marketPlaceInArticle,
    div.teaserEditorialContent
    {
        display:none !important;
        width:0px;
        height:0px;
    }

    div#mainContent,
    div#mainColLeft
    {
        width:100% !important;
    }
    
    a.active
    {
        line-height: 18px;
    }
    
    h1.standard
    {
        font-size: 32px;
    }
    
    div#article
    {
        margin-top: 0px;
    }

    body.modal-open
    {
        overflow: scroll;
    }
}

@-moz-document regexp("https://www\\.tagesanzeiger\\.ch/[^/]+/")
{
    #topNav,
    div#wideBoardContainer,
    div.ad-outside-right,
    div#mainColRight,
    div.ad-inside-full-top,
    div.ad-inside-half,
    div#footerLinks,
    div#footer
    {
        display:none !important;
        width:0px;
        height:0px;
    }
    
    div.bottomLinks
    {
        margin-bottom: 0px;
    }
    
    #topStoryA h3 a
    {
        font-size: 32px;
    }
    
    a.active
    {
        line-height: 18px;
    }
    
    div#mainColLeft
    {
        width:100%;
    }
    
    div.featureStory,
    .mainColLeftSection #stories .featureStory div
    {
        width:100%;
        float:none;
    }

}
