style.css:
@-moz-document domain("manhunt.net") {
body.displayLoginSuccess  {width: 980px}
body.displayLoginSuccess .logoPlusHead {opacity:0; width: 80%}
#supersized, #supersized img, body.displayLoginSuccess .coa-bar, body.displayLoginSuccess .footer {display:none}
form#login {display: block; width: 976px; padding: 0}
form#login fieldset.column-1, form#login fieldset.column-2 {width: 100%}
form#login fieldset.column-1 input#loginusername, form#login fieldset.column-2 input {width:95%; height: 50px; font-size: 30px !important; text-align: center; }
form#login fieldset.column-2 input {margin-bottom: 25px !important}
form#login input#login {display:block; width:96.5% !important; margin-top: 1em; clear: both; line-height: 90px;height: 100px}
}
