style.css:
@-moz-document url-prefix("http://www.academiamir.com/mir/faces/usr/simulacros/usrSimulacrosRepasarPreguntasPregunta.jsp") {
 
/* Descripción 			Estilo CSS para corrección de simulacros de la Academia AMIR para usar con la extensión de navegador Stylish */
/* Stylish for Firefox: 
/* Autor: 					Fadi Ammari */
/* Última versión: 	Oct 2014 */

#body1,.top-tail,.bot-tail,#main,#main > div,#header,#header > div{width:100%!important;height:100%!important;float:none!important;position:static!important;top:0!important;left:0!important;background:transparent!important}
form#form1:before{content:"Academia AMIR";color:#5c8b1b;font-family:"Arimo";font-size:20px;padding:10px;margin-top:30px;display:block;border-bottom:1px solid #ccc}
form#form1{width:95%;margin:0 auto}
form#form1 > table:nth-child(1){display:none}
form#form1 > table:nth-child(2) tbody tr td:first-child,form#form1 > table:nth-child(2) tbody tr td:last-child{display:none}
@media (min-width: 800px) {
form#form1 > table:nth-child(2) td{display:block;width:250px!important}
form#form1 > table:nth-child(2),form#form1 > table:nth-child(3){display:table-cell;width:auto!important}
object{height:200px}
form#form1{width:90%}
}
#header .small-menu,#header .clear,.list-header{display:none!important}
table{width:100%!important;vertical-align:top}
tr.list-row-even td:nth-child(2),tr.list-row-odd td:nth-child(2){display:none}
.fondoPanel{padding:10px;background:transparent!important;border:0;border-radius:0}
form#form1 > table > tbody > tr:nth-child(2) .fondoPanel{border:1px solid #ccc}
.fondoPanel span{font-size:16px!important;line-height:1.3;font-family:"Arimo";letter-spacing:.02em;text-align:right}
#chartSimulacroPregunta2Container{text-align:center}
form#form1 > table > tbody > tr:nth-child(5),form#form1 > table > tbody > tr:nth-child(6){display:none}
#footer div.text{position:fixed;display:block;background-color:#fff;color:#5c8b1b!important;bottom:0;line-height:20px;text-align:center!important;border-top:1px solid #eee;padding:3px 0}
#footer a{color:#5c8b1b!important}

@font-face {
	font-family: 'Arimo';
  font-style: normal;
  font-weight: 400;
  src: local('Arimo'), url(https://fonts.gstatic.com/s/arimo/v8/wZLYhRRxZWGToTRP78bqJQ.woff) format('woff');
}
}
