style.css:
@-moz-document domain("www.thepaper.cn") {
.newsinfo_page .news_txt {
	font-size: 1.5em !important;
	line-height: 1.7em !important;
}

.news_txt * {
font-size: 1em !important;
}
  
.back-to > div {
opacity: 0.2;
transition: opacity .3s ease-in-out;
-moz-transition: opacity .3s ease-in-out;
-webkit-transition: opacity .3s ease-in-out;
}
    
.back-to:hover > div {
opacity: .8 !important;
}
   
.back-to {
margin-right: -551px;
}

#toolBackTop {
opacity: 0.4;
transition: opacity .3s ease-in-out;
-moz-transition: opacity .3s ease-in-out;
-webkit-transition: opacity .3s ease-in-out;
}

#toolBackTop:hover{
opacity: 1 !important;
}
}
