style.css:
@-moz-document domain('lainchan.org'), url-prefix('https://lainchan.org')  {
    body {
       margin: 0 !important;
       padding: 0px !important;
       background: #395168 !important;
       overflow-x:hidden!important;
       background-size: cover !important;
    }
    
    html {
        margin: 0px !important;
        padding: 0px !important;
    }
    .subtitle {
        color: #13C15F !important;
    }
    .prettyprint {
        background: #536F8C  !important;
        padding: 4px !important;
        margin: 0px !important;
        border: 1px solid #395168 !important;
        width: 71% !important;
        display: inline-block !important;
        color: #fff ! important;
    }
    h1, h2{
        color: #D880FC !important ;
    }
    h2 {
        background: #435D78 !important;
        border-bottom: 1px solid #536F8C !important;
    }
    h2:not(:nth-child(1)) {
        border-top: 1px solid #536F8C !important;
    }
    a:hover{
        color: #CC9EDF !important;
        text-shadow: none !important;
    }
    small {
        color: #13C15F !important;
    }
    legend, li {
        color: #D56Edd !important;
    }
    .boardlist a, a {
        color: #D880FC !important;
    }
    .post.reply{
        background: #435D78 !important;
        border: 1px solid #536F8C !important;
        color: #13C15F !important;
    }
    .intro .subject {
        color: #D880FC !important;
    }
      [name="post"] tr *, [name="post"] [type="text"]:not([name="subject"]), #subject, #length, #ip, input[type="checkbox"], form table tr th, .mod, #search, fieldset, checkbox, .box, .ban, .board_image,  select, .pages, textarea, tbody, input, tr, th{
        color: #eee !important;
        background: #435D78 !important;
        border: 1px solid #536F8C !important;
    }
    .pages {
        color: #536F8C !important;
    }
        .pages.top {
        background: #435D78 !important;
    }
    .delete, #style-select, label, footer, .unimportant, .body, .time, .fileinfo, time {
        color: #eee !important;
    }
    p, h4,.name, .blotter, .quote {
        color: #13b04a !important;
    }
    hr {
        border: 1px solid #536F8C !important;
    }
    .banner{        
        color: #CC9EDF !important;
        padding: 5px !important;
        width: 370px !important;
        background: #435D78 !important;
        border: 1px solid #536F8C !important;
    }
    .boardlist {
        text-shadow: none !important;
        background: #435D78 !important;
        border-bottom: 1px solid #536F8C !important;
        padding: 4px !important;
        margin: 0px !important;
    }
    .boardlist.bottom {
        margin-top: 41px !important;
        margin-bottom: 4px !important;
        border-top: 1px solid #536F8C !important;
    }
    .sub {
        color: #536F8C !important;
            
    }
    footer {
        display: none !important;
    }
input[type="submit"]:hover {
background: #435D78 !important;
border: #536F8C 1px solid !important;
color: #eee!important;
}
.post.reply.highlighted, .post.reply:hover{
background: #536F8C !important;
border: transparent 1px solid;
}
}
