/* checkbox */
form.fancy input[type="checkbox"]+label:before,form.fancy input[type="radio"]+label:before {
	border: 0 !important;
	border-radius: 5px !important;
}

form.fancy input[type="checkbox"]:checked+label:before,form.fancy input[type="radio"]:checked+label:before{
	content:"✓";
}

/* 	riot's button css */
body .content .rfmtoolbar .buttons .button {
	height: 30px !important;
	width: 30px !important;
	cursor: pointer !important;
	float: left !important;
	margin-right: 3px !important;
	background-color: transparent !important;
	background-repeat: no-repeat !important;
	background-position: center !important;
	border: 0 !important;
	outline: 0 !important;
	background-image: url("http://boards.na.leagueoflegends.com/images/sprite-master-9b37347acf8dc4694c17d07069369039.png") !important;
	border-radius: 0 !important;
	box-shadow: none !important;
}

body .content .rfmtoolbar .buttons .button.bold{
	background-position: -55px -295px !important;
}

body .content .rfmtoolbar .buttons .button.italic{
	background-position:-193px -228px !important;
}

body .content .rfmtoolbar .buttons .button.strikethrough{
	background-position:-220px -295px !important;
}

body .content .rfmtoolbar .buttons .button.url{
	background-position:-303px -228px !important;
}

body .content .rfmtoolbar .buttons .button.blockquote{
	background-position:-138px -228px !important;
}

body .content .rfmtoolbar .buttons .button.unordered{
	background-position:0px -295px !important;
}

body .content .rfmtoolbar .buttons .button.ordered{
	background-position:-176px -149px !important;
}

body .content .rfmtoolbar .buttons .button.champion{
	background-position:-248px -228px !important;
}

body .content .rfmtoolbar .buttons .button.summoner{
	background-position:-121px -149px !important;
}

body .content .rfmtoolbar .buttons .button.item{
	background-position:-165px -295px !important;
}
