style.css:
@-moz-document domain("weibo.com") {
    #v6_pl_rightmod_updatev6,
    #v6_pl_rightmod_recominfo > :not(:nth-child(2)),
    #v6_pl_rightmod_updatev6 + div[style] > :last-child,
    #v6_trustPagelet_recom_member,
    #v6_pl_rightmod_noticeboard,
    .PCD_mplayer,
    [node-type="feed_list_shieldKeyword"],
    #v6_pl_content_biztips,
    [id^=v6_pl_rightmod_ads],
    #v6_pl_ad_bottomtip,
    #WB_webim {
        display: none;
    }

    [node-type="feed_list_timeTip"] {
        background: none;
        box-shadow: none;
        color: rgba(255, 255, 255, 0.8);
    }

    .PCD_header .S_shadow {
        background: none;
    }
}
