style.css:
@-moz-document domain("wikipedia.org") {
.mw-body-content
{
  font-size:inherit;
  line-height:2.063;
}
.mw-body
{
  max-width:46em;
  margin:-1px auto auto;
}
.mw-body h1, .mw-body h2, .mw-body #firstHeading
{
  margin-bottom:.6em;
}
.hatnote
{
  opacity:.8;
  font-size:.875em;
}
.hatnote a
{
  opacity:1.25;
}
#siteSub
{
  display:none;
}
body
{
  font-size:1.063rem;
}
}
