style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.vso-software.fr") {

head, body, input, select, textarea,
#navigation a, #tabs a, #tabs li a span
{
background: #000000 !important;
}
.post, .button1, .button2,
#navigation a:hover, #tabs li a span:hover
{
background: #222222 !important;
}
blockquote, ul, li, .panel,
.navbar .inner, .rules
{
background: #333333 !important;
}
span.corners-top span, .navbar, .forumbg,
span.corners-bottom span,
.corners-bottom, .corners-top
{
background-image: none !important;
background: #0684C4 !important;
}
span.corners-top,
span.corners-bottom
{
background-image: none !important;
}
.navbar .corners-bottom, .navbar .corners-top
{
background: #000000 !important;
}
.post, .rules
{
border-left: 4px #0684C4 solid !important;
border-right: 4px #0684C4 solid !important;
}
.inputbox
{
border-color: #0684C4 !important;
}
.navbar
{
padding: 0 4px !important;
}
span.corners-top span,
span.corners-bottom span
{
margin-left: 6px !important;
}
a, p, ul, li, dd, dt,
blockquote, label, strong, span, input, select, textarea,
.content, .button1, .button2, .signature,
h1, h2, h3, h4, h5, h6
{
color: #FFFFFF !important;
}
}
