style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nextinpact.com") {
  
  /* Suppression des pubs et des boutons réseaux sociaux du site */
  #container_ban_pub_jauge,
  #sidebar_pub_top,
  #wide_tlf_section,
  #bandeau_abonnez_vous,
  #auth_social_network,
  .pub_encart_content,
  .icon-facebook,
  .icon-gplus,
  .icon-twitter,
  .icon-linkedin,
  .icon-incognito {
      display: none !important;
  }

  /* Suppression des pubs des régies */
  #page-container > a[href*="edi7.lu"] {
    background: none !important;
  }
  
  /* Le décalage vers le haut en cas de pub régie ne focntionne pas */
  #container_hot_social {
    
  }

  /* Déplacement des liens du site */
  #container_social_icon {
    position: fixed;
    top: 8px;
    right: 440px;
    z-index: 1000000; /* histoire d'être tout au dessus ! ;-) */
  }
  
  /* Bloc: Se connecter */
  #connectionLink {
    display: inline-block;
  }
  
  /* Bouton: Se connecter */
  #loginButton,
  #textLogin,
  #connectionLink {
    width: 120px !important;
    height: 25px !important;
    overflow: hidden;
  }
    
}
