style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://uni1.darkfleet.de/darkfleet/internal/map.") {
  body , body.framemapbg > * {
    -webkit-perspective: 500px;
    perspective: 500px;
  }
  #mapWrapper , body.framemapbg > center:nth-child(2) > div:nth-child(1) , body.framemapbg > center:nth-child(3) > div:first-child {
    -webkit-transform: rotateY(0deg) rotateX(50deg) rotateZ(45deg);
    transform: rotateY(0deg) rotateX(50deg) rotateZ(45deg);
    top:-50px;
  }
  #fueldisp {
    -webkit-transform: rotateY(0deg) rotateX(0deg) rotateZ(0deg);
    transform: rotateY(0deg) rotateX(0deg) rotateZ(0deg);
    top:0px;
  }
}
@-moz-document url-prefix("http://uni1.darkfleet.de/darkfleet/internal/frset.") {
}
