style.css:
@-moz-document url-prefix("https://www.tumblr.com") {

body:not(.mceContentBody), html .identity-refresh .l-container.l-container--two-column .right_column, .identity-refresh .l-container.l-container--two-column-dashboard .right_column, .identity-refresh .l-container.l-container--two-column .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .left_column {
background-color: #2C4762 !important;
}

body:not(.mceContentBody) {
  background-color: #2c4762 !important;
background-image: linear-gradient(#395875, #2C4762) !important;
  background-image: linear-gradient(to bottom, rgba(57,88,117,1) 0%,rgba(44,71,98,1) 25%,rgba(44,71,98,1) 100%) !important;
  background-position: top !important;
  background-repeat: no-repeat !important;
  background-size: 100% !important;
}

#dashboard_iframe {
    background: transparent !important;
  }

html .identity-refresh .controls_section li:hover {
      background-color: rgba(255,255,255,0.024) !important;
    }
      
      html .identity-refresh .controls_section.user_list li:hover {
  background: transparent !important;
              }

.tab_bar {
    float: right !important;
  }

.search_popover {
    left: auto !important;
    right: 22px !important;
    top: 44px !important;
    width: 207px !important;
}

.search_popover .result.has_thumb .hide_overflow {
    max-width: 130px !important;
}
            
             .l-header-container--refresh {
  background-color: transparent !important;
      box-shadow: none !important;
                    }
      
      html .identity-refresh .controls_section.user_list li .follow_list_item_blog:before, .identity-refresh .controls_section.user_list li:hover .follow_list_item_blog:before, .identity-refresh .radar .radar_footer .radar_avatar:before {
      		background-image: linear-gradient(to right,rgba(44,71,98,0),#2C4762);
			border-right: 5px solid #2C4762;
              }
      
      html .identity-refresh .right_column:after {
             background-image: linear-gradient(rgba(44,71,98,0),#2C4762); 
            }

            
            .l-header-container--refresh .l-header {
                  min-width: 910px !important;
                  max-width: 910px !important;
                  padding: 0px !important;                                                                                                  
                }
                
                
              .identity-refresh .selected .tab_notice, .l-header-container--refresh .tab_notice, .identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container {
  						border: 2px solid #395875 !important;
                        background: #D95E40 !important;
        				left: 32px !important;
						top: -10px !important;
                      }
                      
                 .identity-refresh .tab_notice .tab_notice_value {
                              color: #fff !important;
                            }

.l-container.l-container--two-column .l-content, .l-container.l-container--two-column-dashboard .l-content {
        padding: 15px 15px 0px 15px !important;
  margin-top: -20px !important;
      }

.l-header-container--refresh {
    padding-top: 9px !important;
    position: absolute !important;
  }
  
  .elevator-wrapper {
      top: 20px !important;
     right: 20px !important;
  height: 55px !important;
    }

	#return_to_bottom {
  top: auto !important;
  		bottom: 20px !important;
}
    
.header-redesign .ui_search, .ui_search {
      float: right !important;
      margin-top: 1px !important;
    }
  
.ui_search.tab.active .search_query, .ui_search.tab.active .search_form_field:before, .ui_search .search_query, .header-redesign .ui_search .search_query {
                height: 30px !important;
            width: 220px !important;
            border-radius: 50px !important;
                background: #2C4762 !important;
              }

.ui_search.active .search_query {
    color: #fff !important;
}
          
          .header-redesign .ui_search .search_form_field:before, .search_form_field:before {
            top: 5px !important;
                    left: 11px !important;
                    opacity: 0.4 !important;
                    color: #fff !important;
                  }

.ui_search.active .search_form_field:before {
    opacity: 1 !important;
}

/* --- Tabs ---*/

.l-header-container--refresh.header-redesign.has-3d-logo .search_popover {
  left: auto !important;
          right: 23px !important;
          top: 43px !important;
        }
        
        .tab-bar-container {
          float: left !important;
            width: 63% !important;
          }
          
          .header-redesign .tab_bar {
        float: right !important;
              }

.tab_home .tab_anchor {
  margin-top: -4px !important;
    }
    
.l-header-container--refresh .l-header .logo.logo-redesign {
          margin-left: 17px !important;
        }

/* --- Body --- */
          
.post_avatar.post-avatar--fixed, .post_avatar.post-avatar--absolute {
  position: absolute !important;
  top: 0px !important;
  left: -85px !important;
    }
    
    .post_avatar.post-avatar--sticky {
      height: auto !important;
        }


.elevator {
  width: 55px !important;
  height: 55px !important;
    background: rgba(255,255,255,0.22) !important;
    border-radius: 10px !important;
  }
  
  
  .elevator:before {
  font-size: 55px !important;
  line-height: 80px !important;
  color: #2C4762 !important;
  margin-left: 14px !important;
      }


.l-header-container--refresh .l-header .compose-button {
        display: none !important;
      }

.identity .right_column {
  margin-top: -4px !important;
    }

/*[[editor]]*/

/*[[logo]]*/

}

options.value:
/* --- Old Logo --- */

.l-header-container--refresh .l-header .logo .logo-anchor {
background-image: url('https://www.tumblr.com/images/logo/logo_small.png') !important;
          background-repeat: no-repeat !important;  
          background-position: left top !important;
          width: 150px !important;
        }
          
          .l-header-container--refresh .l-header .logo {
    		width: 150px !important;
          margin-right: -100px !important;
          margin-left: 20px !important;
          margin-top: -10px !important;
  }
        
         .svg-logo {
                visibility: hidden !important;
  width: 150px !important;
              }

.tab-bar-container {
    margin-left: 150px !important;
    width: 48% !important;
  }

/*-- Text -editor ---*/

.post-form--controls .control.left {
        text-align: right !important;
        float: right !important;
      }
      
      .post-form--controls .control.right {
              float: left !important;
              width: auto !important;
              text-align: left !important;
              margin-top: -30px !important;
            }
      
            .post .post-content .title-field * {
                font-size: 20px !important;
                line-height: 20px !important;
                min-height: 20px !important;
              }
      
                    .title-field, .quote-field {
                    border: 1px solid #ccc !important;
                padding: 7px !important;
                    border-radius: 4px 4px 0px 0px !important;
                    margin-bottom: 0px !important;
                      }
                  
                  .caption-field, .source-field, .chat-field {
                      border: 1px solid #ccc !important;
  margin-top: -1px !important;
                padding: 7px !important;
  padding-top: 39px !important;
                    }
    
    .chat-field {
    margin-top: -31px !important;
  border-top: none !important;
      }
               
                .caption-field .editor-richtext {
              width: 100% !important;
                    }
                
                .post-form--tag-editor {
                margin-top: -1px !important;
                    margin-left: 20px !important;
                    margin-right: 20px !important;
                    border: 1px solid #ccc !important;
                    border-radius: 0px 0px 4px 4px !important;
                padding: 5px 7px !important;
                  }

.post-form--tag-editor .tag-input-wrapper {
  margin-bottom: 2px !important;
    }
             
              .post-form--controls {
              border: none !important;
                }
                
                .editor-slot .bubbles {
                    position: absolute !important;
                    display: inline !important;
                    opacity: 1 !important;
					top: -39px !important;
                      left: -8px !important;
                      max-width: 290px !important;
                  }

.editor-wrapper{
  margin: -7px !important;
   border-top: 1px dashed #ccc !important;
    padding: 7px !important;
  }
  
  .title-field .editor-wrapper, .quote-field .editor-wrapper, .url-field .editor-wrapper, .chat-field .editor-wrapper {
    border: none !important;
      }
                    
                    .bubbles>div, .bubbles>div span {
            display: inline !important;
                opacity: 1 !important;
                        border-radius: 0px !important;
                        color: #bbb !important;
                        background: transparent !important;
                      }
              
              .bubbles>div.active {
                  color: #529ecc !important;
                }
            
            .editor .post .post-content img, .editor .post .post_content img {
              width: 97% !important;
                  }


.editor-slot .inline-controls .opener {
  left: -10px !important;
  color: #bbb !important;
      }

.inline-controls {
  position: absolute !important;
    width: 210px !important;
  top: -35px !important;
  left: 290px !important;
  }
            
                         .tray-open .editor-slot .inline-controls {
                          display: block !important;
  							  z-index: 100000000 !important;
        
                            }

.editor-slot .inline-controls .tray {
                        width: 100% !important;
  						z-index: 100000000 !important;
                        opacity: 1 !important;
  						top: 0px !important;
                    	left: 30px !important;
                    }

                  
                  
                  .editor-slot .inline-controls .tray .control {
            display: inline-block !important;
                            opacity: 1 !important;
              color: #bbb !important;
                          }

.media-dropzone-container {
    margin-bottom: 37px !important;
  }

.html-mode .src-view {
  width: 92% !important;
  margin-left: 15px !important;
    }

.popover--save-post-dropdown {
  z-index: 100000 !important;
  }

.post_full .post_body.post_ask_answer_form_container {
  margin-top: 15px !important;
      }

  .post-form--asks .post-forms--ask-anonymous {
  margin-left: 100px !important;
    }

.post-forms--social-buttons .social-buttons {
  margin-left: 110px !important;
      margin-top: 5px !important;
    }

.media-container {
  margin-bottom: 45px !important;
  }

.ask-form .ask-anonymously {
    margin-left: 80px !important;
    margin-top: 5px !important;
}
