style.css:
@-moz-document url-prefix(http://www.tumblr.com/),url-prefix(https://www.tumblr.com/){
.post_full.is_full_width.is_photo .post_media_photo {height: auto!important; max-width: 500px!important;}
.post_full.is_photoset .photoset .photoset_row .photoset_photo img {width: auto!important;}
.post .post_content img {max-width: 500px!important; height: auto!important;}
.post_media .photoset, .post_media .post_media_photo, .post_media .panorama, .post_media .audio_player_container {margin: 0 20px!important;}
.photoset_row {width: 500px!important; height: auto!Important;}
.photoset_row_1 img {max-width: 500px!Important; min-width: 500px!Important;}
.photoset_row_2 img, .photoset_row.photoset_row_2 img, .photoset_row.photoset_row_2>a>img, .photoset_row.photoset_row_2>img {max-width: 245px!important; min-width: 245px!Important;}
.photoset_row_3 img, .photoset_row.photoset_row_3 img, .photoset_row.photoset_row_3>a>img, .photoset_row.photoset_row_3>img {max-width: 160px!important; min-width: 160px!Important;}
.post_full.is_full_width.is_photoset .photoset .photoset_row .photoset_photo {margin-left: 10px;}
.post_full.is_full_width.is_photoset .photoset .photoset_row, .post_full.is_full_width.is_photo .photoset .photoset_row {margin-top: 10px;}
.photoset_row.photoset_row_1>a>img {width: 500px!important; min-width: 500px!important;}
.post_full.is_photoset .photoset .photoset_row .photoset_photo {margin-left:10px!important;}
.post_full.is_photoset .photoset .photoset_row:first-child, .post_full.is_photo .photoset .photoset_row:first-child {margin-top: 0!important;}
.post_full.is_photoset .photoset .photoset_row, .post_full.is_photo .photoset .photoset_row {margin-top: 10px!important;}
.post .post_content blockquote img {max-width: 100%!important;}
img.inline_external_image.constrained_image.enlarged {max-width: 100%!Important;}
.post_full .post_body .inline_image {max-width: 100% !important; height: auto !important;}
.dashboard-context .full-width-inline-media .post.is_regular .tmblr-full, .tmblr-full  {margin: 0!important;}
.dashboard-context .full-width-inline-media .post.is_regular .tmblr-full>img, .dashboard-context .full-width-inline-media .post.is_note .tmblr-full>img, .dashboard-context .full-width-inline-media .tmblr-full>img {min-width: 0!important; width: auto!important; max-width: 500px!important;}
.post_full .post_media_small>img.post_media_photo.image {margin: 0!important;}

}
