style.css:
@-moz-document domain("tumblr.com") {
.identity-refresh .selected .tab_notice, .identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container, ._3LtFh {
background: #D95E3F;
-webkit-animation: rainbow 15s infinite alternate;
-moz-animation: rainbow 15s infinite alternate;
}

.identity-refresh .tab_notice .tab_notice_value {
color:#FFF;
}

/*Firefox*/
@-moz-keyframes rainbow {
      0% {background-color: #D95E3F;}
     15% {background-color: #EB71BD;}
     30% {background-color: #913FD9;}
     45% {background-color: #3F6DD9;}
     60% {background-color: #3FBAD9;}
     75% {background-color: #45BF41;}
     90% {background-color: #E7AD31;}
     100% {background-color: #EB942C;}
}

/*Safari and Chrome*/
@-webkit-keyframes rainbow {
      0% {background-color: #D95E3F;}
     15% {background-color: #EB71BD;}
     30% {background-color: #913FD9;}
     45% {background-color: #3F6DD9;}
     60% {background-color: #3FBAD9;}
     75% {background-color: #45BF41;}
     90% {background-color: #E7AD31;}
     100% {background-color: #EB942C;}
}
}
