style.css:
@-moz-document domain('github.com') {
.issue-link[title]:after {
  content:attr(title);
  font-size: 0.8em;
  margin-left: 0.4em;
  margin-right: 0.6em;
  padding: 0.2em 0.4em;
  border: 1px solid #ccc;
  background: #eee;
  color: #777;
  position: relative;
  top: -1px;
}
}
