style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("scour.com") {

div[class="sponsoredlinks"],
div[id="sponsoredlinks"],
a[id="uservoice-feedback-tab"],
div[id="topcornerfeedback"],
div[id="adsbar"],
div[id="scour-tip"],
div[style="height: 30px;"],
div[id="adsbottom"],
div[id="featuredAds"],
div[class="sponsoredlinks"] {

  display: none !important;
  max-width:0px !important; 
  }

span[id="pagerPrev"],
a[id="pagerNext"],
a[title="Next Page"],
a[title="View Page 1"],
a[title="View Page 2"],
a[title="View Page 3"] {

  -moz-border-radius: 3px;
  }

a[id="pagerNext"]:hover,
a[title="View Page 1"]:hover,
a[title="Next Page"]:hover,
a[title="View Page 2"]:hover,
a[title="View Page 3"]:hover {

  background: #DDD !important;
  border: 1px solid #000 !important;
  color: #000 !important;
  }

div[class="resultTitleRow"] a:hover {

  color: #FFF !important;
  background: #2693CF !important;
  }

a:hover[class="resultDomainLink"] {

  color: #FFF !important;
  background: #207CAF !important;
  -moz-border-radius: 2px !important;
  }

div[id="results"] { 

  width: 985px !important;
  margin: 0 10px 0 0 !important;
  }

div[class="searchStats"] {

  width: auto !important;
  }

input[id="txtSearch"],
input[id="txtSearchB"] {

  width: 450px !important;
  font-size: 14pt !important;
  padding: 4px 10px 0 !important;
  }

div[id="submenu"] { 
  
  background-image: url(data:image/png;base64,) !important;
  border: 2px solid #D9DFE3 !important;
  -moz-border-radius: 6px !important;
  }

body {

  border-top:0px solid #8BC300 !important;
  margin:0 auto !important;
  max-width: 1000px !important;  
  }

div[class="l_url"] {

  font-family:Georgia,serif !important;
  }

div[class="searchbox"] {

  border: 2px solid #45A2CD !important;
  -moz-border-radius: 6px !important;
  width: 450px !important;
  height: 32px !important;
  padding-top: 4px !important;
  padding-bottom: 5px !important;
  background: #A4CDEF !important;
  }

div[class="searchcontainer"] {

  padding: 7px 0 0 5px !important;
  }

div[class="searchtabs"] {

  margin-bottom: 0px !important;
  }

div[id="relatedterms"] {

  padding-top: 0px !important;
  }

div[id="submenuTitle"] {

  padding-top: 7px !important;
  }

div[id="listing"] {

  max-width:1200px !important;
  }

div#rightsidecell {
    
  display: none !important;
  }
}
