style.css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* allow any number of columns in Customize Toolbar box */
#palette-box {
  float: left !important;
  overflow-x: hidden !important;
}
#palette-box .paletteRow {
  display: inline !important;
}
#palette-box toolbarpaletteitem {
  display: inline !important;
  vertical-align: middle !important;
  width: 105px !important;
  height: 65px !important;
  margin: 0 !important;
  padding: 5px !important;
}

/* make all buttons' text be on the bottom - thanks Grist! */
#palette-box toolbarpaletteitem toolbarbutton {
  -moz-box-orient: vertical !important;
  text-align: center !important;
  width:105px!important;
}

#palette-box > hbox > toolbarpaletteitem > label{color: #0000E0!important;}
#palette-box > hbox > toolbarpaletteitem .toolbarbutton-text{color: #303030!important}

/* show button that AIOS hides by mistake */
#wrapper-console2-button #console2-button {
  display: -moz-box !important;
}

/* bg-color for toolbar and palette items */
toolbarpaletteitem[place="toolbar"]:hover,
toolbarpaletteitem[place="palette"]:hover {
background-color: #FFFFCC !important;
border: dashed 1px black !important;
margin:-1px !important;
padding: 0 !important;
}
toolbarpaletteitem[place="palette"]{
border: solid 1px transparent !important;
}
