style.css:
@-moz-document url("http://www.imgur.com"), domain("imgur.com") {
/* Imgur Space Theme */
/**/
/**/
/* http://drydareelin.deviantart.com */



#fullbleed-bg {
width: 100%;
min-width: 1000px;
height: 1200px;
background: url(http://fc03.deviantart.net/fs71/f/2013/182/4/0/solitary_by_drydareelin-d6bhfwn.png) top center repeat;
max-width: 100%;
}

#topbar {
background-color: rgba(0,0,0,0.1);
box-shadow: 0 0 0px 0px #111;
-webkit-box-shadow: 0 0 0px 0px #111;
-moz-box-shadow: 0 0 0px 0px #111;
height: 0px;
z-index: 1;
}
.textbox {
background-color: rgba(255,255,255,0.1);
-moz-border-radius: 0px;
-webkit-border-radius: 0x;
border-radius: 0px;
}
#top-comments .textbox {
border-radius: 0px 0px 0 0;
margin-bottom: 0px;
}


.posts {
background-color: rgba(255,255,255,0.1);
box-shadow: 0 0 0px 0px #111;
-webkit-box-shadow: 0 0 0px 0px #111;
-moz-box-shadow: 0 0 0px 0px #111;
margin-bottom: 0px;
}
.br10 {
border-radius: 0px;
-moz-border-radius: 0px;
-webkit-border-radius: 0px;
}
#top-comments #outside-tagging {
width: 385px;
border-radius: 0px;
-moz-border-radius: 0px;
-webkit-border-radius: 0px;
box-shadow: 0 0 0px 0px #111;
-webkit-box-shadow: 0 0 0px 0px #111;
-moz-box-shadow: 0 0 0px 0px #111;
}
#outside-tagging {
background-color: rgba(255,255,255,0.1);
border-radius: 0 0px 0px 0;
-moz-border-radius: 0 0px 0px 0;
-webkit-border-radius: 0 0px 0px 0;
}
.panel {
background-color: rgba(255,255,255,0.1);
-webkit-border-radius: 0px;
-moz-border-radius: 0px;
border-radius: 0px;
}
.best-captions .caption-container {
background: rgba(255,255,255,0.1);
}
#outside-tagging-showhide {
background-color: rgba(255,255,255,0.1);
-webkit-border-radius: 0 0px 0px 0;
-moz-border-radius: 0 0px 0px 0;
border-radius: 0 0px 0px 0;
right: -33px;
}
body {
background: url(http://fc03.deviantart.net/fs71/f/2013/182/4/0/solitary_by_drydareelin-d6bhfwn.png) top center repeat;
color: #FAFFBF;
}
.social, .icons {
background-color: rgba(255,255,255,0.1);
border-radius: 0px;
-moz-border-radius: 0px;
-webkit-border-radius: 0px;
}
.jcarousel {
background-color: rgba(255,255,255,0.1);
border: 0px solid #444442;
border-radius: 0px;
}
.carousel-button {
background-color: rgba(255,255,255,0.1);
border-radius: 0px;
-moz-border-radius: 0px;
-webkit-border-radius: 0px;
height: 170px;
}

.right .panel.menu .active {
border-bottom: 0px solid transparent;
border-right: 0px solid rgba(255,255,255,0.5);
border-top: 0px solid transparent;
height: 0;
left: -12px;
position: absolute;
top: 0;
width: 0;
display: none;
}

.navNext {
color: #fff;
width: 95px;
border-left: 0px solid #232323;
border-radius: 0 0px 0px 0;
background-color: rgba(255,255,255,0.1);
opacity: .5;
background: rgba(255,255,255,0.1);
}
.navPrev {
border-radius: 0px 0 0 0px;
}

.navPrev, .navBrowse, .navTop, .submit-caption-button {
background: rgba(255,255,255,0.05);
}
.navPrev:hover, .navBrowse:hover {
background: rgba(255,255,255,0.1);
}
.next-prev {
border: 1px solid #404040;
float: left;
border-radius: 0px;
-moz-border-radius: 0px;
-webkit-border-radius: 0px;
}
.navBrowse, .navTop {
margin-left: 5px;
border: 1px solid #404040;
border-radius: 0px;
-moz-border-radius: 0px;
-webkit-border-radius: 0px;
width: 100px;
}
.navTop:hover {
background: rgba(255,255,255,0.1);
}

.fixed-share-container .social {
background-color: rgba(255,255,255,0.05);
}
.fixed-share-container .twitter_custom, .fixed-share-container .facebook_custom, .fixed-share-container .googleplus_custom, .fixed-share-container .reddit_custom, .fixed-share-container .pinterest_custom, .fixed-share-container .st_sharethis_custom {
-moz-border-radius: 0px;
-webkit-border-radius: 0px;
border-radius: 0px;}
.fixed-share-container .facebook_custom {
background: rgba(59,89,153,0.5);
}
.fixed-share-container .facebook_custom:hover {
background: rgba(59,89,153,1);
}
.fixed-share-container .twitter_custom {
background: rgba(85,172,238,0.5);
}
.fixed-share-container .twitter_custom:hover {
background: rgba(85,172,238,1);
}
.fixed-share-container .reddit_custom .reddit-icon {
background-image: url(http://imgur.com/images/site-sprite.png?1410226436);
opacity: 0.5;
}
.fixed-share-container .reddit_custom:hover .reddit-icon {
background-image: url(http://imgur.com/images/site-sprite.png?1410226436);
opacity: 1;
}
.fixed-share-container .st_sharethis_custom {
-moz-border-radius: 0px;
-webkit-border-radius: 0px;
border-radius: 0px;
margin-bottom: 0px;
}
#submit-caption textarea {
background-color: rgba(0,0,0,0.5);
margin: 0;
font-size: 1.2em;
}
#submit-caption textarea:focus {
background-color: rgba(0,0,0,0.75);
    }
#side-gallery-controls .selection {
border-radius: 0px 0px 0 0;
background-color: rgba(255,255,255,0.1);
border: 1px solid #404040;
border-bottom: 0 none;
}
#side-gallery .nano {
background: rgba(255,255,255,0.1);
margin-top: -6px;
height: 528px;
border-radius: 0 0 0px 0px;
-moz-border-radius: 0 0 0px 0px;
-webkit-border-radius: 0 0 0px 0px;
}
.post {
background-color: rgba(255,255,255,0.05);
border: 1px solid #404040;
display: inline-block;
margin: 4px 2px;
position: relative;
}
.main-image .image {
text-align: center;
background-color: rgba(255,255,255,0);
padding: 0;
margin: 0;
line-height: 0;
min-height: 10px;
position: relative;
border-radius: 0px;
-moz-border-radius: 0px;
-webkit-border-radius: 0px;
}
#comment-sort {
padding: 0;
border: 0;
background-color: rgba(255,255,255,0);
width: 45px;
}
.green {
color: #B0C683 !important;
}
.green:hover {
color: #526032 !important;
}
#meme-save-button, #new-meme-button, #defaults .selection {
background: rgba(255,255,255,0.1);
border: 1px solid #404040;
border-radius: 0px;
color: #ddddd1;
}

#meme-save-button:hover, #new-meme-button:hover, #defaults:hover .selection:hover {
background: rgba(255,255,255,0.5);
border: 1px solid #404040;
border-radius: 0px;
color: #ddddd1;
}

#sort {
background-color: rgba(255,255,255,0);
}
#sort.combobox:hover .selection {
background-color: rgba(255,255,255,0.1);
}
.about-content p, .team-content p, .assets-content p {
color: #000000;
background: rgba(255,255,255,0.1);
}
  .about-content p:hover, .team-content p:hover, .assets-content p:hover {
color: #ddddd1;
background: rgba(255,255,255,0.1);
}
.bg-secondary {
background-color: rgba(255,255,255,0.1);
}
.bg-dark {
background-color: rgba(0,0,0,0.05);
border-top: 1px solid #121211;
border-bottom: 1px solid #121211;
}
#footer {
background: rgba(0,0,0,0.75) url(http://imgur.com/images/blog/footer-logo.png) no-repeat;
background-position: center center;
background-size: 150px 38px;
}
.long-excerpt-container p strong {
color: #121211;
}

.btn, #comments input[type=submit], #searchsubmit {
-moz-box-shadow: inset 0 0 0 0px #6fb320;
-webkit-box-shadow: inset 0 0 0 0px #6fb320;
box-shadow: inset 0 0 0 0px #6fb320;
background-color: rgba(255,255,255,0.1);
-webkit-border-radius: 0px;
-moz-border-radius: 0px;
border-radius: 0px;
border: 1px solid #474647;

-webkit-box-shadow: 0 0 0px 0px rgba(0,0,0,.2);
-moz-box-shadow: 0 0 0px 0px rgba(20,0,0,.2);
box-shadow: 0 0 0px 0px rgba(20,0,0,.2);
}

.category-banner {
top: 0;
max-width: 100%;
height: 215px;
background: #000 url(http://imgur.com/images/blog/default-banner.jpg?1407982386) no-repeat;
      opacity: 0.5;
background-size: cover;
background-position: center center;
}

#cboxTopCenter {
height: 40px;
background: rgba(0,0,0,0.5);
-webkit-border-top-left-radius: 0px;
-webkit-border-top-right-radius: 0px;
-moz-border-radius-topleft: 0px;
-moz-border-radius-topright: 0px;
border-top-left-radius: 0px;
border-top-right-radius: 0px;
border: 1px solid #444442;
border-bottom: 0 none;
position: relative;
}
.popup {
padding: 10px;
background-color: rgba(0,0,0,0.5);
}
#cboxContent {
border: 1px solid #444442;
border-top: 0 none;
background: rgba(0,0,0,0.5);
}

input[type=button].button-medium, input[type=submit].button-medium, button.button-medium, .button-medium {
background: rgba(255,255,255,0.1);
border-radius: 0px;
border: 1px solid #444442;
}
input[type=button].button-medium:hover, input[type=submit].button-medium:hover, button.button-medium:hover, .button-medium:hover {
background: rgba(0,0,0,0.5);
border-radius: 0px;
border: 1px solid #444442;
}
#cboxContent, #cboxLoadedContent {
-webkit-border-bottom-right-radius: 0px;
-webkit-border-bottom-left-radius: 0px;
-moz-border-radius-bottomright: 0px;
-moz-border-radius-bottomleft: 0px;
border-bottom-right-radius: 0px;
border-bottom-left-radius: 0px;
}


.date-text {
background-color: rgba(0,0,0,0.1);
padding: 3px;
padding-left: 25px;
font-weight: 700;
font-size: 12px;
}
.striped {
background-color: rgba(0,0,0,0.25);
}
.reply-message {
border-color: rgba(0,0,0,0.5);
}
.thread-wrapper .thread-bottom {
border-radius: 0 0 0px 0px;
padding: 0 0 10px;
background-color: rgba(0,0,0,0.5);
position: relative;
}
#conversations-list .top {
height: 35px;
background-color: rgba(0,0,0,0.5);
border-top-left-radius: 0px;
border-top-right-radius: 0px;
margin-top: 10px;
overflow: hidden;
border-bottom: 1px solid #444442;
}
#conversations-list .conversation {
background-color: rgba(0,0,0,0.5);
cursor: pointer;
padding-left: 10px;
padding-right: 15px;
border-bottom: 1px solid #444442;
border-top: 1px solid #444442;
padding-bottom: 10px;
padding-top: 10px;
position: relative;
}
#conversations-list .bottom {
height: 20px;
background-color: rgba(0,0,0,0.5);
border-top: 1px solid #444442;
border-bottom-left-radius: 0px;
border-bottom-right-radius: 0px;
}

.favorite-icon {
display: inline;
background-color: rgba(255,255,255,0);
border-radius: 0px;
overflow: auto;
margin-right: 3px;
padding: 4px;
float: left;
text-align: center;
}

.btn, .main-image .image-arrows .arrow.pushed, .main-image .image-arrows .arrow:active, .favorite-image:active, .favorite-image.favorited {
background-color: rgba(255,255,255,0.1);
float: left;
border-radius: 0px;
color: #fff;
position: relative;
opacity: .8;
margin-right: 4px;
line-height: 29px;
font-size: 16px;
cursor: pointer;
background: rgba(0,0,0,0.5);
}

.textbox {
background-color: rgba(255,255,255,0);
-moz-border-radius: 0px;
-webkit-border-radius: 0x;
border-radius: 0px;
}

#captions .caption .arrows {
background-color: rgba(0,0,0,0.5);
margin: 0 3px 0 0;
padding: 3px 3px 4px;
color: #AAA;
float: left;
width: 20px;
height: 42px;
border-radius: 0px;
-moz-border-radius: 0px;
-webkit-border-radius: 0px;
}
}
