style.css:
@-moz-document url("http://www.tumblr.com/"), url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
* {
    text-shadow: none !important;
    color: #818181 !important;
}
.post-forms-glass.post-forms-glass_active.active {
    background-color: rgba(204, 204, 204, .70);
}
.svg-logo {
    background-image: url('/*[[logo]]*/ /*[[soul]]*/ /*[[selfish]]*/') !important;
    background-size: contain !important;
    width: 70px !important;
}
.logo .large-shadow, 
.logo .small-shadow, 
.logo .outer, 
.logo .inner {
    display: none;
}
#content {
    background-color: inherit!important;
    background: none!important;
    opacity: 0.0!important;
    filter: alpha(opacity=0)!important;
    background-image: none !important;
}
.l-header-container--refresh {
    box-shadow: none !important;
}
.identity-refresh .l-container {
    padding-top: 60px;
}
.l-header-container--refresh .l-header {
    min-width: 900px;
    width: 100%!important;
    max-width: 920px;
    height: 38px!important;
}
.identity-refresh .inbox_notice_container,
.identity-refresh .new_post_notice_container {
    border: none!important;
    background-color: #cccccc !important;
    opacity: 0.6!important;
    filter: alpha(opacity=60)!important;
    min-width: 18px;
    height: 18px;
}
.l-content {
    background: url('http://i.imgur.com/QY9bzua.png') top no-repeat!important;
}
        
/*[[gradient]]*/ /*[[sugar]]*/ /*[[fish]]*/ /*[[emo]]*/ /*[[cake]]*/ /*[[lune]]*/ /*[[japan]]*/ 

/*[[dreams]]*/ /*[[mellon]]*/ /*[[terra]]*/ /*[[pancake]]*/

ol#posts li.notification {
    background-color: #f9f9f9 !important;
    border-bottom: none !important;
    color: #f9f9f9 !important;
}
#right_column:after,
#right_column,
#left_column {
    padding: 20px 0 !important;
}
.logo_dot {
    background-color: inherit!important;
    background: none!important;
    opacity: 0.0!important;
    filter: alpha(opacity=0)!important;
    background-image: none !important;
}
#right_column:after,
#right_column,
#left_column,
.post_content,
.post_tags:after,
.section.first.divider,
blockquote pre,
#tumblr_radar {
    background: none !important;
}
.search_popover {
    border: none !important;
    border-top: none !important;
    max-width:362px;
}
.identity-refresh .send_fan_mail {
    display: block !important;
    padding: 8px 0;
    margin: 0 0 20px;
    color: #fff;
    text-align: center;
    font-size: 15px;
    font-weight: 700;
    text-decoration: none;
    -webkit-font-smoothing: antialiased;
    border-radius: 0px;
    background: none;
    background-color: inherit !important;
}
.send_fan_mail:hover {
    background: #b3b3b3 !important;
    opacity: 0.70 !important;
}
#right_column a#dashboard_switch_blog_menu_current {
    background: #f9f9f9 !important;
    opacity: 0.90 !important;
    border: 0px !important;
}
#right_column:hover {
    display: block;
    border-bottom: none !important;
    background-color: inherit !important;
    background: none!important;
    background-image: none !important;
    border: none !important;
}
.election-day-voted .popover_blogs {
    margin-top: 66px;
    position: relative;
    opacity: 0.8 !important;
}
.identity-refresh .popover_blogs {
    margin-top: 66px;
    opacity: 0.8 !important;
}
.election-day-voted .popover_blogs:hover,
.identity-refresh .popover_blogs:hover {
    background-color: inherit !important;
    opacity: 0.8 !important;
}
.discover-controls-wrapper {
    display: block;
    border-bottom: none !important;
    background-color: inherit !important;
    background: none!important;
    background-image: none !important;
    border: none !important;
    opacity: 0 !important;
}
.search_popover {
    margin-left:40px;
    margin-top:-11px;
    background-color: inherit !important;
    opacity: 0.8 !important;
}
.search_popover:hover {
    background-color: inherit !important;
    opacity: 0.8 !important;
}
.popover_menu_item:hover,
.search_popover .popover_menu_item_link:hover,
.search_popover .explore_topics_link:hover,
.search_popover .result_link:hover {
    background: none !important;
    background-color: #d9d9d9 !important;
    opacity: 0.8 !important;
}
.identity-refresh .controls_section li:not(.popover_blogs):hover {
    background-color: inherit !important;
    background: none !important;
    opacity: 1.0 !important;
    text-shadow: 2px !important;
    color: #818181 !important;
}
.identity-refresh .controls_section li:not(.popover_blogs) {
    background-color: inherit !important;
    background: none !important;
    opacity: 0.6 !important;
}
#nav .nav_item.active {
    background-color: #d9d9d9;
}
.identity-refresh .controls_section.user_list li .follow_list_item_blog:before {
    background-color: inherit!important;
    background: none!important;
    opacity: 0.0!important;
    filter: alpha(opacity=0)!important;
    background-image: none !important;
}
#left_column #posts .post .permalink {
    background-color: #d9d9d9 !important;
    opacity: 0.90 !important;
}
.post_tags {
    margin-top: 0px !important;
}
.post_content {
    padding-bottom: 10px !important;
}
#left_column .left_column {
    background-color: inherit!important;
    background: none!important;
    opacity: 0.0!important;
    filter: alpha(opacity=0)!important;
    background-image: none !important;
}
.popover_menu_item_tracked_tag:hover {
    background: #b3b3b3 !important;
}
.post_avatar {
    opacity: 1.0 !important;
    filter: alpha(opacity=100)!important;
    background-color: inherit !important;
}
.post_avatar .post_avatar_link {
    border-radius: 0px !important;
}
.post_avatar_link {
    opacity: 0.80 !important;
    filter: alpha(opacity=80)!important;
    background-color: #d9d9d9 !important;
}
.post_avatar_link:hover {
    opacity: 1.0 !important;
    filter: alpha(opacity=100)!important;
    background-color: #d9d9d9 !important;
}
#nav .nav_item.active,
#nav .nav_item .nav_menu,
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
    background-color: #f9f9f9 !important;
    background-image: none !important;
    border: 0px !important;
}
#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current {
    background-image: none !important;
}
#permalink {
    background-image: none !important;
}
.identity-refresh .post_full .post_permalink {
    border-color: #b3b3b3 #b3b3b3 transparent transparent;
    border-radius: 0 0 0 5px;
    border-style: solid;
    position: absolute;
    border-width: 0px;
    background: #ccc;
    display: block;
    opacity: 0;
    height: 0;
    width: 0;
    right: 0;
    top: 0;
    -webkit-transition: border-width 150ms ease-in, opacity 0 ease-in 150ms;
    -moz-transition: border-width 150ms ease-in, opacity 0ms ease-in 150ms;
    transition: border-width 150ms ease-in, opacity 0ms ease-in 150ms;
}
.l-header .selection_nipple {
    border-bottom: none !important;
    display: none !important;
    background-color: inherit !important;
    border: 0px !important
}
.l-header .selection_nipple:hover {
    border-bottom: none !important;
    display: none !important;
    background-color: inherit !important;
    border: 0px !important
}
.l-header-container {
    display: block;
    border-bottom: none !important;
    background-color: inherit !important;
    border: 0px !important;
    background: none!important;
    background-image: none !important;
}
.l-header-container--refresh {
    display: block;
    border-bottom: none !important;
    background-color: inherit !important;
    border: 0px !important;
    background: none!important;
    background-image: none !important;
}
.compose-button {
    display: block;
    border-bottom: none !important;
    background-color: inherit !important;
    border: 0px !important;
    background: none!important;
    background-image: none !important;
    opacity: 0.6!important;
    filter: alpha(opacity=60);
    fill:#818181;
}
.compose-button .notification_type_icon {
    display: none !important;
}
.ui_search .search_form_field:before {
    left: 5px!important;
    top: 5px!important;
}
.search_form_row {
    background-color: inherit!important;
    opacity: 0.6!important;
    filter: alpha(opacity=60);
    background-image: none !important;
}
.ui_search .search_query:hover {
    background-color: #f9f9f9 !important;
    opacity: 0.90;
}
#left_column #posts .notification {
    background-color: #d9d9d9 !important;
    color: #666 !important;
    border: 0px solid;
    border-color: #cccccc !important;
    opacity: 0.50;
}
.video_preview.no_thumbnail,
.link_button {
    background-color: #b3b3b3 !important;
    border: none !important;
}
.chrome.blue {
    background-color: inherit !important;
    background: none !important;
    border: none !important;
    opacity: 0.8;
    font-size: 16px;
}
.chrome.blue:hover {
    opacity: 1.0 !important;
}
#new_post_notice_container {
    color: #fff !important
}
#new_post_notice_container a {
    color: #fff !important
}
#content {
    padding: 10px 0;
}
}
