style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("kickass.to")
{

.valueBubbleUser
  {
    border: 2px solid black !important;
  }
  
.valueBubbleUser:hover
  {
    border: 2px solid black !important;
  }
  
html body div#wrapper div#wrapperInner header {
background-image: url("https://yuq.me/users/38/488/Wo1wMaUZ4P.png") !important;
}
  
#navigation li .userImage
  {
    border: 3px solid transparent !important;
  }
  
header a#logo
  {
    background: url("/*[[logo]]*/") !important;
  }
#navigation li .dropdown
  {
    background-image: url("https://yuq.me/users/38/488/Wo1wMaUZ4P.png") !important;
  }
#navigation li a:hover
  {
    background-image: url("https://yuq.me/users/38/488/Wo1wMaUZ4P.png") !important;
  }
#navigation li a
  {
    background-image: url("https://yuq.me/users/38/488/Wo1wMaUZ4P.png") !important;
  }
#navigation a
  {
    color: /*[[colour]]*/ !important;
  }
#navigation li .dropdown .ka16
  {
    background: none !important;
  }
.nickname
  {
    color: black !important;
    text-shadow: 2px 2px 2px #400000 !important;
  }

.badgeInline
  {
    color:/*[[colour]]*/;
  }
  
.chat-bar
  {
    background-image: url("https://yuq.me/users/38/488/Wo1wMaUZ4P.png");
  }

.chat-bar li
  {
    border-right:1px solid /*[[colour]]*/ !important;
  }
 
.barUser:hover
  {
    background:darkgrey;
  }

.chat-bar li:hover {
    text-decoration: none;
    cursor: pointer;
    background-image: url("https://yuq.me/users/38/488/Wo1wMaUZ4P.png") !important;
}

.valueBlock
  {
    background:black !important;
    color: /*[[colour]]*/ !important;
    border: /*[[colour]]*/ solid 1px;
  }

/*[[chatbox]]*/

/*[[fulltheme]]*/

}

options.value:
.chat-container
  {
    background-image: url("https://yuq.me/users/38/488/Wo1wMaUZ4P.png") !important;
    color: white !important;
  }
.chat-title a
  {
    color: red !important;
  }
.chat-message
  {
    background-image: url("https://yuq.me/users/38/488/Wo1wMaUZ4P.png") !important;
  }
.chat-history
  {
    background-image: url("https://yuq.me/users/38/488/Wo1wMaUZ4P.png") !important;
  }
.chat-unread
  {
    background-image: url("https://yuq.me/users/38/488/Wo1wMaUZ4P.png") !important;
  }
.chat-input
  {
    background-image: url("https://yuq.me/users/38/488/Wo1wMaUZ4P.png") !important;
  }
.chat-close, .chat-minimize
  {
    background: none !important;
    border: red solid 1px;
  }

ul.tabNavigation li .darkButton:hover
  {
    color: red !important;
    background: lightgray !important;
  }
ul.tabNavigation li .darkButton
  {
    color: red !important;
    background: black !important;
  }
.profileCloud
  {
    background: gray;
  }
.siteButton, .icon16
  {
    background: black;
  }
.updatedStatus
  {
    color: black;
  }
.icon16:hover
  {
    background: black;
  }
.siteButton:hover, .icon16:hover
  {
    color: black;
    background: lightgrey;
  }
