style.css:
/*AGENT SHEET*/
@-moz-document url-prefix("about:stylish-edit"),url-prefix("data:text/html") {

/*_______________
 |               |
 | BUTTON LAYOUT |
 |_______________|*/

#stylish-edit.theme-body #preview-button { 
    -moz-box-ordinal-group: 0 }
#stylish-edit.theme-body #check-for-errors-button { 
    -moz-box-ordinal-group: 1 ;
    margin-right: 4px !important;
    margin-left: 0px !important; }
#stylish-edit.theme-body #wrap-lines { 
    -moz-box-ordinal-group: 2 }
#stylish-edit.theme-body .devtools-separator { 
    -moz-box-ordinal-group: 3 ; 
    -moz-box-flex: 1; 
    background: transparent !important }
#stylish-edit.theme-body button[label="Insert"] { 
    -moz-box-ordinal-group: 4 ;
    margin-right: 0px !important }
#stylish-edit.theme-body #save-button { 
    -moz-box-ordinal-group: 5 } 

/*______________________________
 |                              |
 | BACKGROUND & TOOLBAR MARGINS |
 |______________________________|*/

#editor, 
.CodeMirror-dialog,
.devtools-monospace,
.CodeMirror-gutter.CodeMirror-foldgutter,  
.CodeMirror-gutter.CodeMirror-linenumbers {
    background: -moz-linear-gradient(#414141 .5%,#141414 7%,#121212 92%,#040404 98%) fixed !important; }
#editor-tools {
    margin-top: -6px !important;
    background: #414141 !important;
    border-top: 0px solid #414141 !important }
#main-area >hbox,
#main-area >grid >rows > row:first-child { 
    background: #414141 !important }
#main-area >grid >rows > row:first-child { 
    margin-bottom: -1px !important }
#main-area.theme-toolbar row:first-child { 
    outline: 1px solid  #414141 }
#main-area.theme-toolbar       { 
    background: #040404 !important }
.devtools-monospace            { 
    margin-left: -5px !important }
#main-area.theme-toolbar #name { 
    margin-right: 3px !important }
#main-area.theme-toolbar >grid { 
    margin-top: -6px !important }
.devtools-horizontal-splitter  { 
    opacity: 0 }
.CodeMirror-gutter.CodeMirror-foldgutter { 
    border-right: 1px solid rgb(90,90,90) !important }

/*_______
 |       |
 | FONTS | 
 |_______|*/

/*[[fx_fonts]]*/

* { cursor: default }

/*_________
 |         |
 | BUTTONS |
 |_________|*/
    
#save-button,
#preview-button,
#check-for-errors-button,
#editor-tools button[label="Insert"] {
    color: #c8c8c8 !important;
    margin-top: 4px !important;
    border-radius: 0px !important;
    box-shadow: none !important;
    -moz-appearance: none !important;
    border: 1px solid #323232 !important;
    background: -moz-linear-gradient(#9b9b9b 2%,#696969 10%,#464646 90%,#141414 98%) !important; }
    
#save-button:not([disabled="true"]):hover,
#preview-button:not([disabled="true"]):hover,
#check-for-errors-button:not([disabled="true"]):hover,
#editor-tools button[label="Insert"]:hover {
    color: #f0f0f0 !important;
    border: 1px solid #141414 !important;
    background: -moz-linear-gradient(#bebebe 2%,#7d7d7d 10%,#505050 90%,#1e1e1e 98%) !important; }
    
#save-button:hover:active,
#preview-button:hover:active,
#check-for-errors-button:hover:active,
#editor-tools button[label="Insert"]:hover:active {
    background: -moz-linear-gradient(#A0915A 2%,#6E5A28 10%,#503C1E 90%,#1e1e1e 98%) !important; }

window[sizemode="normal"] :-moz-any(#save-button,#preview-button,#check-for-errors-button),
window[sizemode="normal"] #editor-tools button[label="Insert"] { margin-top: 4px !important }

#save-button, 
#preview-button, 
button[label="Insert"],
#check-for-errors-button { outline-color: transparent !important }
    
.devtools-toolbarbutton hbox  { border-color: transparent !important } 

/* ADDS DOTTED OVERLAY FOR DISABLED BUTTONS */
#stylish-edit.theme-body :-moz-any(#save-button,#preview-button,#check-for-errors-button)[disabled="true"] {
    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAIGNIUk0AAHolAACAgwAA+f8AAIDpAAB1MAAA6mAAADqYAAAXb5JfxUYAAAAJcEhZcwAAHB4AABweAbK65j4AAAAYdEVYdFNvZnR3YXJlAHBhaW50Lm5ldCA0LjAuM4zml1AAAAAdSURBVBhXY/j//7+9nJwcMwwzwACGAEwlmgwDAwCOgQlh8muKBgAAAABJRU5ErkJggg==),
    -moz-linear-gradient(#9b9b9b 2%,#696969 10%,#464646 90%,#141414 98%) !important; 
    opacity: 1 !important; } 

/*_________________________________
 |                                 |
 | SHORTENS CHECK FOR ERRORS LABEL |
 |_________________________________|*/

#check-for-errors-button > hbox:after {
    content: 'Check Errors' ; 
    display: -moz-box ;
    color: #c8c8c8 ;
    margin-right: -3px ;}
#check-for-errors-button > hbox:before {
    content: '__' ;
    display: -moz-box ;
    color: #c3c3c3 ;
    font: normal 10px Segoe UI ;
    margin: 0 -8px -3px -1px }
#check-for-errors-button { margin-right: 1px !important }
#check-for-errors-button > hbox > children > label { display: none }
#check-for-errors-button:not([disabled="true"]):hover hbox:after  { color: #f0f0f0 }
#check-for-errors-button:not([disabled="true"]):hover hbox:before { color: #e6e6e6 }

/*_____________________
 |                     |
 | TWEAKS INSERT LABEL |
 |_____________________|*/ 

button[label="Insert"] .dropmarker-icon {
   margin-right:  -6px !important }
button[label="Insert"]  .button-text{
   margin-right:  -12px !important }

/*___________________________
 |                           |
 | SHORTENS WRAP LINES LABEL |
 |___________________________|*/

#wrap-lines > image {
    margin-right: 2px ;
    opacity: .8 ;
    -moz-appearance: none !important; }
#main-area #wrap-lines {
    margin: 0 -1px 0 -3px !important; }
#main-area #wrap-lines > hbox > label {
    display: none }
#main-area #wrap-lines > hbox:after {
    content: 'Wrap' ; 
    display: -moz-box;
    color: #dcdcdc ;
    margin: 0 0px 0 -1px }
#main-area #wrap-lines > hbox:before {
    content: '__' ; 
    display: -moz-box;
    color: #c8c8c8 ;
    margin: 3px -10px 0 0 ;
    font: normal 10px Segoe UI }

/*_________
 |         |
 | TEXTBOX |
 |_________|*/

#main-area.theme-toolbar label[control="name"] {
    margin: 0 2px 0 185px !important;
    color: #d2d2d2 ;
    padding-left: 2px }
#main-area #name .textbox-input-box {
    padding-bottom: 1px ;
    margin: -2px 0 0 -2px }
#main-area.theme-toolbar #name .textbox-input {
    margin-bottom: -1px !important; }
#main-area #name {
    border: 1px solid #323232 !important;
    font-size: 13px !important;
    color: #c8c8be !important;
    background: -moz-linear-gradient(#9b9b9b 2%,#696969 6%,#5a5a5a 97%,#000 99.5%) !important; }

/*__________________
 |                  | 
 | COLLAPSIBLE CODE |
 |__________________|*/

.CodeMirror-foldgutter-open.CodeMirror-guttermarker-subtle {
    font-weight: bold !important;
    color: rgb(160,160,160) !important }
   
.CodeMirror-foldgutter-folded.CodeMirror-guttermarker-subtle  {
    font-weight: bold !important;
    color: red !important }
  
.CodeMirror-foldmarker { 
    font-size: 18px !important;
    color: white !important;	
    text-shadow: 1px 1px 4px red, -1px -1px 4px red, 1px 1px 4px red, -1px -1px 4px red !important;
    padding-left: 1px !important;
    padding-right: 1px !important } 


/*______________
 |              |
 | ERRORS POPUP |
 |______________|*/

#main-area #errors {
    max-height:242px !important; 
    min-height: 24px !important;
    padding: 4px 0px !important;
    background: -moz-linear-gradient(rgb(110,110,110) 1%,rgb(50,50,50) 6%,rgb(30,30,30) 92%,rgb(5,5,5) 99%) }
#main-area #errors > label {
    color: rgb(250,10,0) !important;  
    font: normal 14px/14px Tahoma, Geneva, sans-serif !important;
    font: normal 12px/13px "Segoe UI" ;/*original style*/ }
#main-area #errors > label:nth-child(1) {
    margin-top: -1px !important; }   
#main-area #errors > label:hover {
    color: rgb(210,210,210) !important; } 

/*____________________
 |                    |
 | AUTOCOMPLETE POPUP |
 |____________________|*/
    
.devtools-autocomplete-popup { border: none !important; background: -moz-linear-gradient(#7d7d7d 4%,#414141 10%,#373737 92%,#141414 99%) !important }
.devtools-autocomplete-popup > richlistbox > richlistitem[selected] > label.autocomplete-value { color: #00C800 !important }
.devtools-autocomplete-popup > richlistbox > richlistitem[selected]    { background-color: rgba(250,250,250,.1) !important }
.devtools-autocomplete-popup > richlistbox > richlistitem:hover:not([selected]) { background-color: transparent !important }
.devtools-autocomplete-popup > richlistbox > richlistitem > label { color: #000 !important }  

/*_________________________
 |                         |
 | FINDBAR POPUP & RESULTS |
 |_________________________|*/

.CodeMirror-dialog {
    padding-left: 10px !important;
    color:  #aaa !important;
    font: normal 90% lucida console !important;
    border-color: rgba(20,20,20,.7) !important;
    box-shadow: 0px -1px 1px rgba(255,255,255,.1) inset !important }
.CodeMirror-dialog input {
    width: 85% !important;
    padding: 0px 0 !important;
    margin-left: 6px !important;
    color: #c8c8aa !important;
    font: normal 110% lucida console !important;
    border-right: 1px solid  rgba(20,20,20,.7) !important }
.cm-searching {
    background: rgba(250,0,0,.5) !important }

/*______________
 |              |
 |  MENU POPUPS |
 |______________|*/

menupopup {
    box-shadow: none !important;
    -moz-appearance: none !important;
    border-radius: 0px !important;
    color: #a0a0a0 !important;
    border: none !important;
    padding: .4em 0 !important;
    background: -moz-linear-gradient(#7d7d7d 1%,#4b4b4b 3%,#323232 97%,#141414 99.5%) !important;
    text-shadow: 0px 1px 0px rgba(0,0,0,.15), 0px -1px 0px rgba(0,0,0,.3), 2px 2px 1px rgba(200,200,200,.2) !important }
menupopup .menu-text,
menupopup .menu-iconic-text {
    font: normal 14px candara !important;
    color: #c8c8c8 !important }
menuitem {
    -moz-appearance: none !important;
    box-shadow: none !important  }
menuitem[selected="true"]:not(hover) {
    background: transparent !important }
menuitem:hover,
menuitem[selected="true"]:hover { 
    background: rgba(0,0,0,.4) !important }

/*_________________________________________________________________________________________________________
 |                                                                                                         |
 | SCROLLBARS PORTED FROM https://userstyles.org/styles/106537/scrollbar-graphite-autohiding-latest-wip    |
 | NOTE: made changes to the code April 8th 2016, nolonger disappears when first installed. For some reason|
 | now only floats on the page with the codemirror, so so part of the page in the standard editor used     |
 | in Palemoon & SEM, otherwise a big improvement nolonger needing to reload pages.                        |
 |_________________________________________________________________________________________________________|*/

/*AGENT_SHEET*/
scrollcorner { visibility: collapse !important }
scrollbar > scrollbarbutton, .CodeMirror-scrollbar-filler { display: none !important }
scrollbar[orient='vertical'] >  slider > thumb { min-height: 28px !important }
scrollbar[orient='horizontal'] > slider >thumb { min-width: 28px !important }
    
/* COMPATIBILITY FIX FOR DEVTOOLS DARK THEME */
.theme-dark  scrollbar[orient="vertical"] {
  padding-bottom: 14px !important;
  -moz-margin-start: -18px !important;
  min-width: 14px !important;
  max-width: 14px !important }
.theme-dark scrollbar[orient="horizontal"] { 
  margin-bottom: 4px !important;   
  margin-right: 14px !important;
  margin-top: -18px !important;
  min-height: 14px !important;
  max-height: 14px !important; }
.theme-dark #errors > scrollbar[orient="vertical"] {
  padding: 0px !important;
    margin-right: 2px !important;} 

.theme-light scrollbar[orient="vertical"] {
  padding-bottom: 14px !important }   
  
scrollbar > slider > thumb,
scrollbarbutton[sbattr="scrollbar-up-top"],
scrollbarbutton[sbattr="scrollbar-down-bottom"] {
    border-radius: 0px !important;
    display: -moz-box !important;
    -moz-appearance: none !important;
    border: 1px outset #000 !important;
    background-color: #464646 !important;
    box-shadow: inset 1px 1px 6px 0px #191919, inset -1px -1px 6px 0px #b2b2b2 !important; }
scrollbar:not(#foo)  {
    background-color: #414141 !important;
    box-shadow: inset 2px 2px 3px 1px rgba(25,25,25,.6), inset -2px -2px 3px 1px rgba(178,178,178,.6) !important; }
scrollbar > slider > thumb:-moz-any(:hover, :active),
scrollbarbutton:-moz-any(:hover, :active)[sbattr="scrollbar-up-top"],
scrollbarbutton:-moz-any(:hover, :active)[sbattr="scrollbar-down-bottom"] {
    background-color: #a0a0a0 !important; }

/* HIDES SCROLL BUTTONS TILL HOVERED */
scrollbar:not(:hover) scrollbarbutton      { opacity: 0 !important; }

/* HIDES HORIZONTAL THUMB TILL HOVERED */
:not(#foo) scrollbar[orient="horizontal"]:not(:hover),
scrollbar[orient="horizontal"]:not(:hover) { opacity: 0 !important; }

/* KEEPS VERTICAL THUMB VISIBLE */
:not(#foo) scrollbar[orient="vertical"]:not(:hover),
scrollbar[orient="vertical"]:not(:hover)   { opacity: 1 !important; }

/* COMMENT OUT TO SHOW BACKGROUND SLIDER */
scrollbar, scrollbar > slider, html scrollbar:not(#foo),
#internal-code > hbox > textarea > div > scrollbar,
#errors > scrollbar[orient="vertical"] {
    -moz-appearance: none !important;
    background: transparent !important;
    background-color: transparent !important;
    box-shadow: none !important; } 
/*_______________________________________
 |                                       |
 |  DEVTOOLS DARK THEME COLORS - DEFAULT |
 |  if you wanto install other themes,   |
 |  unless theyre added below this code, |
 |  just remove important tags           |
 |_______________________________________|*/

.cm-s-mozilla .cm-meta,
.cm-s-mozilla .cm-hr,
.cm-s-mozilla .cm-comment   { color: #707D8C !important }
.cm-s-mozilla .cm-number    { color: #5c9966 !important }
.cm-s-mozilla .cm-attribute,
.cm-s-mozilla .cm-variable,
.cm-s-mozilla .cm-def,
.cm-s-mozilla .cm-property,
.cm-s-mozilla .cm-qualifier { color: #3689b2 !important }
.cm-s-mozilla .cm-builtin,
.cm-s-mozilla .cm-tag,
.cm-s-mozilla .cm-header    { color: #a673bf !important }
.cm-s-mozilla .cm-bracket,
.cm-s-mozilla .cm-keyword   { color: #a18650 !important }
.cm-s-mozilla .cm-string,
.cm-s-mozilla .cm-string-2  { color: #b26b47 !important }
.cm-s-mozilla .cm-atom,
.cm-s-mozilla .cm-quote,
.cm-s-mozilla .cm-error     { color: #bf5656 !important }
.CodeMirror pre,
.cm-s-mozilla .cm-variable-2,
.cm-s-mozilla .cm-variable-3,
.cm-s-mozilla .cm-operator,
.cm-s-mozilla .cm-special   { color: #a9bacb !important }
.CodeMirror-gutters         { border: none !important }
div.CodeMirror span.CodeMirror-matchingbracket    { color: #fff !important }
div.CodeMirror span.CodeMirror-nonmatchingbracket { color: #FF2323 !important }
.cm-s-mozilla .CodeMirror-lines .CodeMirror-cursor{ border-left: solid 1px #fff !important }
.cm-s-mozilla div.CodeMirror-linenumber { font-family: dejavu sans mono; color: #aaa !important }

/*[[cm_themes]]*/
/*__________________________________________
 |                                          |
 |  OTHER PARTS OF CODEMIRROR CUSTOM LABEL  |
 |__________________________________________|*/

.devtools-separator:before  {
    /*[[cm_theme_label]]*/
    display: -moz-box !important;
    padding: 7px 0 0 0px !important;
    font: italic bold 15px dejavu serif !important;
    text-shadow: 0px 1px 0px rgba(0,0,0,.4),0px -1px 0px rgba(0,0,0,.4),1px 2px 1px rgba(0,0,0,.3)}

