/*____________________________ 
 |_|________________________|_|
 | |                        | |
 | | PALEMOON COMPATIBILITY | |
 |_|________________________|_|
 |_|________________________|_|*/

#internal-code > hbox {
    padding-left: 6px !important;     
    margin: -4px -7px -3px -9px !important;
    background: -moz-linear-gradient(#414141 .5%,#141414 7%,#121212 92%,#040404 98%) scroll !important; 
    }
#save-button,
#preview-button,
#check-for-errors-button,
#editor-tools button[label="Insert"] {
    margin-bottom: 2px !important; 
    }
#check-for-errors-button > hbox > label { 
    display: none 
    }
#main-area #name .textbox-input-box {
    margin: 0px 0px 0px 1px 
    }
#wrap-lines > image {
    margin-right: 3px 
    }
#main-area #name {
    box-shadow:none!important
    }
#preview-button,
#save-button { 
    margin-left: 2px !important
    }
#stylish-edit.theme-body button[label="Insert"] { 
    margin-right: 1px !important 
    }
#name.devtools-textinput { 
    padding-bottom: 4px !important
    }
#findbar.theme-toolbar { 
    background-image:none!important;
    }
.findbar-find-fast {
    font-size: 12px !important;
    color: #B2B2AB !important;
    }
#findbar > hbox > textbox {
    border-radius: 1px !important;
    -moz-appearance: none !important;
    -moz-box-flex: 1 !important;
    max-width: 215px !important;
    margin: 1px 4px 2px 4px !important;  
    padding-bottom: 4px !important;   
    border: none !important;
    background-image: -moz-linear-gradient(#8C8C8C 2%,#424242 6%,#383838 95%,#000 99.5%) !important;
    }
#findbar > hbox > textbox[status="notfound"] {
    box-shadow: none !important;
    color: rgb(200,200,200) !important;
    text-shadow: none !important;
    background: -moz-linear-gradient(rgb(152,135,80) 1%, rgb(92,75,28) 8%, rgb(72,55,8) 92%, rgb(20,20,20) 99%) !important
    }
#findbar > hbox > textbox > hbox > input > div {
    color: #c8c8be;
    font-size: 13px !important;
    }
#findbar > hbox > textbox > hbox > input {
    padding-bottom: 1px !important;
    margin-top: -1px !important; 
    }
label[value="Find:"] { 
    display: none !important 
    }
.findbar-find-next.tabbable,
.findbar-find-previous.tabbable{
    color: #AAA !important;
    box-shadow: none !important;
    -moz-appearance: none !important;    
    border-radius: 1px ; border: 0 !important;
    background: -moz-linear-gradient(#696969 2%,#333 12%,#191919 88%,#000 98%) !important 
    }
.findbar-find-next.tabbable { 
    margin: 0px 4px 0px 0px !important;
    }
.findbar-find-next.tabbable label {
    margin: 0px 0px 0px -4px !important; 
    }
.findbar-find-previous.tabbable {
    margin: 0px -4px 0px 0px !important;
    }
.findbar-find-previous.tabbable label {
    display: none 
    }
.findbar-find-previous.tabbable:after {
    content: 'Prev' ; color: #aaa;
    display: -moz-box;
    margin: -2px 0px 0 -3px 
    }
.findbar-find-previous.tabbable:before {
    content: '__' ; color: #aaa;
    display: -moz-box;
    margin: 1px -24px 0 0 ;
    padding-left: 16px !important;
    font: normal 10px Segoe UI 
    }
.findbar-find-previous.tabbable:hover:after,
.findbar-find-previous.tabbable:hover:before {
    color: #f0f0f0 !important 
    }
.findbar-find-next.tabbable:hover,
.findbar-find-previous.tabbable:hover{ 
    color: #f0f0f0 !important;
    background: -moz-linear-gradient(#7F7F7F 2%,#474747 12%,#262626 88%,#000 98%) !important
    }
.findbar-find-next.tabbable:hover:active,
.findbar-find-previous.tabbable:hover:active { 
    background: -moz-linear-gradient(rgb(130,110,70) 2%, rgb(75,55,25) 12%, rgb(60,40,10) 88%, rgb(10,10,10) 98%) !important
    }
#findbar > hbox * { 
    margin-top: 0px !important; 
    }

/*________________________________________________________________________________________________________________________________
 |                                                                                                                                | 
 |       ::: NEW SECTION REQUIRED FOR USERSTYLES INSTALL KEY TO ALLOW ANOTHER SECTION WITH NEW URL FOR PALEMOON FINDBAR :::       |
 | OPTION 1: have it autohide when not used, theres actually two workable methods, the 1st is better, autohides everything except |
 | textbox when nothings entered, the 2nd then autohides everything after 5 secs, to try that uncomment rules below using the *   |
 |________________________________________________________________________________________________________________________________|*/ 
@-moz-document url-prefix("about:stylish-edit"),url-prefix("data:text/html") {

#findbar:not(hover) .findbar-find-fast.find-status-icon, 
#findbar:not(hover) .findbar-find-fast.findbar-find-status,
#stylish-edit.theme-body :-moz-any(.findbar-find-next,.findbar-find-previous)[disabled="true"] {
    transition-property: opacity; opacity: 0 
    }
#findbar:hover .findbar-find-fast.find-status-icon,
#findbar:hover .findbar-find-fast.findbar-find-status {    
    transition: opacity 1s 1s; opacity: 1 
    } /*
#findbar > hbox:not(hover) *  { 
    transition: opacity 0s 5s; opacity: 0 
    }
#findbar > hbox:hover *  { 
    transition: opacity 0s; opacity: 1 
    } */
/*________________________________________________________________________________________________________________________________
 |                                                                                                                                |
 | OPTION 2: collapse the findbar and use the standard browwser version - less buggy - just focus on the urlbar & Crl+F to launch |
 | so dont use this option in windowed mode, it also uses the same autohide approach above, only showing textbox when not used    |
 | - the stylish-url qualifiers mean the black background only shows on stylish urls, & only on 100 styles, add more if needed    | 
 |________________________________________________________________________________________________________________________________|*/ 
/* Note: have left out the browser findbar reskin with the autohide only option, 
   just adds another 200 lines - but to try it out without reskin, uncomment this */
/* #findbar.theme-toolbar { visibility: collapse } */

}
