style.css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
/* Remove Blue Junk */

.identity-refresh .l-container.l-container--two-column .right_column, .identity-refresh .l-container.l-container--two-column-dashboard .right_column, .identity-refresh .l-container.l-container--two-column .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .left_column, .l-header-container--refresh, .identity-refresh .controls_section li:not(.section_header):hover, .identity-refresh .post_avatar, .collection_carousel .blogs_wrapper_carousel:before, .collection_carousel .blogs_wrapper_carousel:after, .discover-controls-wrapper, .discover .blog_row:hover, #search_actions_search .elevator, #discover_actions_discover .elevator, .rapid-recs-container .rapid-recs {
  background: transparent;
}
    
.identity-refresh .controls_section.user_list li .follow_list_item_blog:before, .identity-refresh .controls_section.user_list li:hover .follow_list_item_blog:before, .identity-refresh .right_column:after, .identity-refresh .radar .radar_footer .radar_avatar:before, .discover .blog_row .info:before, .discover .blog_row:hover .info:before {
  border-color: transparent;
  background: transparent;
}

.identity-refresh .post_avatar .post_avatar_link
{
  background-color: transparent !important;
}

/* Main Element Colors */

.identity-refresh .post_full .post_permalink, .identity-refresh .post_full .post_permalink:not(.no-transition--dogear) {
  border-color: #757ADB #757ADB #AAAAFF #AAAAFF;
  background: transparent;
}

.identity-refresh .tab_notice {
  background-color: #FFF;
}

.plus-follow-button, .l-header-container--refresh .tab_notice, .identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container, .identity-refresh .tab_notice .tab_notice_value {
  color: #757ADB;
  border-color: #757ADB;
}

.plus-follow-button:hover, .identity-refresh .controls_section .item:hover .follow:hover, .compose-button, .identity-refresh .send_fan_mail, .chrome, .chrome.blue, .chrome.blue:active, .split .chrome.blue[disabled], .split .chrome.blue[disabled]:active, .split .chrome.blue.ui_disabled, .split .chrome.blue.ui_disabled:active, .chrome.blue[disabled], .chrome.blue.disabled, .chrome.blue[disabled]:active, .chrome.blue.disabled:active, .flat-button.blue, .flat-button.blue:active, .flat-button.blue[disabled], .flat-button.blue.disable, .flat-button.blue[disabled]:active, .flat-button.blue.disabled.active {
  background-color: #AAAAFF;
  border-color: #AAAAFF;
}

.identity-refresh .search_results_container .tumblelog_mask_item, #toaster .toast .toast_outer, .identity-refresh .search_landing_outer, .identity-refresh .search_landing_inner_full_wrapper:before, .identity-refresh .collection_carousel .tumblelog_mask_item {
  background-color: #757ADB !important;
}

.glass.blue, #hello_glass.blue, #glass_overlay.blue, .identity-refresh .search_landing_inner, .identity-refresh .controls_section li:not(.no_push):not(.section_header).selected, .identity-refresh .controls_section li:not(.no_push):not(.section_header).selected:hover, .discover-controls-wrapper, .identity .controls_section li:not(.section_header):not(.selected_blog):active, .identity .controls_section li:not(.section_header):not(.selected_blog):hover, .post-forms-glass {
  background: rgba(117,122,219,0.9);
}

.ui_search .search_query, .notification:hover, .identity-refresh .plexi.show.color {
  background-color: rgba(117,122,219,0.6);
}

.identity-refresh .l-container.l-container--two-column-dashboard .right_column, .notification, .dashboard_options_form, .tag_psa {
  background-color: rgba(117,122,219,0.4);
}

.discover .tags-and-tumblelogs {
  padding: 6px;
  margin-bottom: 20px;
  border-radius: 3px;
  background-color: rgba(225,230,255,0.4);
}

/* Text Colors Mostly */

.identity-refresh .controls_section .control-anchor, .identity-refresh .controls_section a .count, .identity-refresh .controls_section .sub_control, .identity-refresh .controls_section .open_blog .open_blog_link, .identity-refresh .controls_section .currently_selected_blog, .identity-refresh .right_column .small_links a, .identity-refresh .controls_section li.account_header, .identity-refresh .controls_section li.editors_header, .identity-refresh .controls_section li.contributors_header, .identity-refresh .controls_section li.section_header, .identity-refresh .controls_section.user_list li .follow_list_item_blog .tumblelog_name, .identity-refresh .controls_section.user_list li .follow_list_item_blog .tumblelog_title, .notification, .notification .notification_sentence a, .notification .notification_sentence a.username, .identity-refresh .controls_section .well, .identity-refresh .controls_section .well:hover, .radar .radar_tumblelog_label .name, .radar .radar_tumblelog_label .subheading, #toaster .toast.toast_image_text_image .toast_inner .txt, #toaster .toast.toast_image_text_icon .toast_inner .txt, #toaster strong, #toaster b, #toaster a, #toaster blockquote, .dashboard_options_form, .tag_psa, .delete_all_messages, .controls_section .controls_item_anchor, .identity-refresh .right_column .small_text, .sidebar_link, .identity-refresh .controls_section .item .icon_close:hover, .no_posts_found, .search_controls .search_control_link, .search_controls .search_control_button, .search_control_items .launch_button .icon_arrow_carrot_down, .results_end_body, .tag_controls .track_tag_wrapper a, .search_landing_tumblelogs_header, .trending_tags_text .trending_tag:hover, .trending_tags_text .trending_tag:active, .trending_tags_text .trending_tag.previewed, .search_controls, .identity-refresh .controls_section.related_tags .tag, .discover-controls-wrapper .content-control, .search_sub_header .search_control.related_searches, .search_sub_header .search_control.related_searches .search_control_link, .xtag .result_title {
  color: rgba(225,230,255,0.8) !important;
}

.identity-refresh .controls_section li, .identity-refresh .controls_section .popover_button_blogs, .identity-refresh .controls_section li.section_header, .identity-refresh .right_column .small_links, .dashboard_options_form, .tag_psa, .tag_controls {
  border-color: rgba(225,230,255,0.4);
}

.compose-button, .compose-button:active, .compose-button:focus, .identity-refresh .send_fan_mail {
  color: #757ADB;
}

.compose-button:active, .compose-button:focus {
  background: #AAAAFF;
  border-color: #AAAAFF;
}

#dashboard_controls a, #dashboard_controls .control, /*#pagination a,*/ .dashboard_options_form .dashboard_options_form_disclaimer, .dashboard_options_form .dashboard_options_form_disclaimer a, .post_full .post_avatar .queue .publish_info.time {
  color: #D8D9FF;
}

#dashboard_controls a, #dashboard_controls .control {
  text-shadow: #AAAAFF 1px 1px 1px;
}

/*#pagination a:hover,*/ #dashboard_controls a:hover, #dashboard_controls .control:hover, .results_end_body .search_query_highlight, .tag_controls .tag, .post_full .post_avatar .queue .publish_info.day, .search_sub_header .search_term_heading {
  color: #E0E5FF;
}

.plus-follow-button {
  background-color: rgba(225,230,255,0.4);
}

.notification blockquote {
  color: rgba(225,230,255,0.6);
  border-color: rgba(225,230,255,0.6);
}

.identity-refresh .controls_section .item .icon_close, .trending_tags_text .trending_tag {
  color: rgba(225,230,255,0.4);
}

.l-footer .footer_links .footer_link a, .xkit-reply-button, .elevator {
  color: #6D6DD7 !important;
}

.identity-refresh .controls_section a canvas {
  opacity: 0.5;
}

.search_results_container .blogs_wrapper_carousel .iScrollIndicator, .collection_carousel .blogs_wrapper_carousel .iScrollIndicator {
  background-color: rgba(225,230,255,0.8);
}

.binary_switch input[type="checkbox"]:checked ~ .binary_switch_track {
  background: #AAAAFF;
}

.discover .discover-section-title, .discover .blog_row .name, .discover .blog_row .title, .discover .tags-and-tumblelogs .discover-tumblelogs .show-more-blogs {
  color: rgba(117,122,219,0.8);
}

.discover .discover-section-title, .discover .blog_row {
  border-color: rgba(117,122,219,0.8);
}

/* Main Backgrounds */

.identity-refresh, .full_page_landing.search_landing .l-footer-container {
  background-color: #D8D9FF;
  background-size: 24px 24px;
  background-repeat: repeat;
  background-position: center 10px;
}
  
.l-header-container--refresh, .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .l-content, .identity-refresh .l-container.l-container--two-column .l-content, .identity-refresh .search_landing_inner_full_wrapper:before {
  background-color: #757ADB;
  background-size: 24px 24px;
  background-repeat: repeat;
  background-position: center 10px;
}

.l-content, .identity-refresh .l-container.l-container--two-column-dashboard .l-content, .identity-refresh .l-container.l-container--two-column .l-content {
  background-position: center 14px;
  padding: 10px;
}

/*[[bgp]]*/


}
