style.css:
@-moz-document
  domain('syosetu.com'),  domain('pdfnovels.net') {
      body {
          font-family:'Hiragino Kaku Gothic Pro', Meiryo, 'MS PGothic', Lucida Sans, sans-serif !important;
      }
      *, *:active, *:focus {
          outline: none !important;
          font-weight: normal !important;
          text-decoration: none !important;
      }
      a:link {color: #258FB8 !important;}
      a:visited {color: #999 !important;}
      a:visited[href^="/search.php?genre="] {color: #258FB8 !important;}
      a:hover {color: #BBB !important;}
      
      #container, #head_koukoku {
          box-shadow: 0px 0px 5px rgba(0,0,0,0.3);
          width: 950px !important;          
      }
      .main_box, .sub_box, .extra_box, .sub1, .top_right, ul.r_navilist li, #bookmarker, #favnovel, #favuser, #writter_menu, #home, .normal_box, #user_box, ul.writter_menu, .yomoumain_box, .searchkekka_box, .comment_main, #pdf_contents, .site_h2 {
       border: solid 1px rgb(191,191,191);
       border-radius: 0px !important;
       background: #FAFAFA;
      }
      #contents, .in_box, .link_box, .ranking_inbox, ul.list_g a, ul.list_p a, .newreview a, .main2_box, .guide_bl, .guide_br {background: none !important;}
      
      .ad, iframe, .bigkoukoku, #direct, div.main_box:nth-child(6), .mobile, div#title br, .osusume, .koukoku2, .koukoku_header, .koukoku_468, .koukoku_160, .koukoku_728, .koukoku728, #mobile a, #userglobal_koukoku /*,[class^="koukoku-"]*/ {display :none !important;}
      
      #header {padding: 5px 0;}
      #head_info {float: left; clear: none !important; font-weight:normal !important;}
      
      /*タイトルロゴ*/
      #title_name {height: 55px !important; margin-top: 2px !important;}
      /*メニュー*/
      #main_navi {
          background: none repeat scroll 0 0 #436f92 !important;
          padding: 0 !important;
          width: 950px !important;
          height: 35px !important;
          border: none !important;
      }
      ul.menu a {
          color: #FFF !important;
          font-weight: normal !important;          
      }
      ul.menu li {font-size: 88% !important; padding-top: 8px !important;}
      ul.menu li a:hover {background: none !important; color: #7ffffa !important;}
      
      /*コンテンツ*/
      div#contents div#main div.main_box div.in_box a span:hover {color: #999 !important;}
      
      #novel_header {background: #FFF !important; height:40px !important;}
      .novelview_navi {width: 900px !important;}
      .novel_bar {padding-top: 10px !important;}
      #user_box a {background: #FFF !important;}
      
      .a_line_t {background: none repeat scroll 0 0 rgba(0,0,0,.05) !important;}
      .a_line_t  a {color: #777 !important;}
      .a_line_t  a:hover{color: #444 !important;}
      input#searchinput:focus, input[type="text"]:focus {border: 1px solid #999 !important; box-shadow: 0 0px 3px #CCC !important;}/*検索focus*/
      
      /*ランキング*/
      a.tl {
          color: #0033CC !important;
          font-weight: normal !important;
          text-decoration: none !important;
      }
      a.tl:hover {color: #C71585 !important;}
      a.tl:visited {color: #999 !important;}
      .ranking_ll a, p.ranking_a_line a, .genreranking_ll a, dl.r_genretypelist dd a {color: #258FB8 !important;}
      .ranking_ll a:hover, p.ranking_a_line a:hover {color: #AAA !important;}
      .ranking_top5box li {margin-bottom: 10px !important;}
      .ranking_top5box li .tl:hover {color: #C71585 !important;}
      
      .ex {color: #333 !important;}
      
      #ranking_navi {font-size: 85%; margin: 10px 0 0;}
      
      ul.ranking_navi {border-bottom: none !important;}
      ul.ranking_navi li {
          width: 150px;
          text-align: center;

      }
      ul.ranking_navi li a::before {content: none !important;}      
      ul.ranking_navi li a {color: #000 !important;}
      ul.ranking_navi li {background: #e6ecf2 !important; border: 1px solid #436f92 !important;border-bottom: none !important;}
      ul.ranking_navi li.on {background: #FFF !important; }
      ul.ranking_navi li.on a {color: #000 !important;}
      
      .r_noveltypelist ul li a {color: #258FB8 !important;}
      
      .ranking_number {font-size: 14pt; vertical-align: -1px !important; width: 55px !important; text-align: right; margin: 0 10px 0 -15px !important;}
      .deadlink_t {font-size: 16px; color: #e38ac2}/*削除アラート*/
      .deadlink {color: #C71585;}
      .move_bar a, ul.r_noveltypelist li a {color: #258FB8 !important;}
      
      ul.ranking_navi {padding-bottom: 0 !important; border-bottom: 1px solid #436f92 !important;}      
      ul.r_navilist a, ul.r_navilist a:visited {color: #000 !important;}
      ul.r_navilist a:hover {color: #67C1F5 !important; border-bottom:none !important;}
      ul.r_navilist li.on {background: none repeat scroll 0 0 #333 !important; border-bottom: none !important;}
      ul.r_navilist li.on a {color: #FFF !important;}
      ul.r_navilist li.change_rank, ul.r_navilist li.second_rank {background-color: #ddd}
      
      dl.rank_novelinfo dd:nth-last-of-type(1), dl.rank_novelinfo dd:nth-last-of-type(2) {width: 200px !important;}
      
      .yomoumain_box {width: 742px !important;}
      .rank_attention2 {font-size: 90%}
      #home a, #home a:hover {background: none !important;}
      
      a.reco1:hover {background: #204D60 !important;}
      .searchkekka_box {margin: 20px 5px 30px 0px !important; padding: 10px !important;}
      
      /*小説Pick up*/
      .list_p_title:hover {color: #666 !important;}
      ul.list_p a:hover {background: none repeat scroll 0% 0% rgba(48, 95, 128, 0.1) !important; color: #258FB8 !important;}
      .list_p_ex {color: #555 !important;}
  }

