style.css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

.tabbrowser-tabs *|tab .tab-close-button {
  display: none;
}

.tabbrowser-tabs *|tab:hover .tab-close-button {
  display: block;
}
.tabbrowser-tab .tab-background{
  background-image: none !important;
}


.tabbrowser-tab[selected] {
  text-shadow: 0px 0px 3px white;
  font-weight: bold;
  background: Highlight;
}

/********************/
/*** PRIVATE TABS ***/
/********************/
/* Background tab */
.tabbrowser-tab[privateTab-isPrivate] > .tab-stack > .tab-background {
  background-color: #AAA;
  background-image: none !important;
}

.tabbrowser-tab[privateTab-isPrivate][selected="true"] > .tab-stack > .tab-background {
  background-color: #CB00F5;
  background-image: none !important;
}

/* Color for hovered provate tabs */
.tabbrowser-tab[privateTab-isPrivate]:hover > .tab-stack > .tab-background {
  background-image: none !important;
  background-color: #E0B0FF;
}

/** The Fox Only Better - Integration **/
[privateTab-selectedTabIsPrivate="true"] #nav-bar,
[privateTab-selectedTabIsPrivate="true"] #theFoxOnlyBetter-slimChrome-slimmer {
  background-color: #CB00F5 !important;
  background-image: none !important;
}

[privateTab-selectedTabIsPrivate="true"] #urlbar {
  background-color: #E6E6FA !important;
  background-image: none !important;
}

