  .single #content div.wp-caption {
    margin-bottom: 0;
}
  
  #articole .wp-caption {
    background: transparent;
    max-width: 97%;
    padding: 0;
    position: relative;

  }
 
   #articole .wp-caption-text {
     font-size: 14px;
     font-style: italic;
     font-family: Cambria, Georgia, serif;
     color: #007faa!important;
     font-weight: normal;
     position: absolute;
     right: 0;
     bottom: 2.5em;
     background: rgba(11, 11, 11, 0.9);
     padding-left: 1em;
     padding-bottom: 0.3em;
     padding-right: 1em;
   }
  
  /*====NAVIGATIE ARTICOLE====*/   
  .pagebar {
  padding-top: 1em;
  padding-bottom: 1em;
  }
  
