  #comments_template {
  clear: both;
    width: 630px;
    padding: 15px;
    background: #262626;
    font-family: Helvetica, Arial, sans-serif;
    color: #c1c1c1;
  }

  #comments_template .commentlist {
    margin-left: 0px;
