style.css:
@-moz-document url-prefix("https://kinogo.cc/") {
/* ПалитРА */
:root {
    --mainbg: /*[[mbg]]*/;
    --maina: /*[[bg]]*/99;
    --mainaa: /*[[bg]]*/33;
    --mtext: /*[[mtext]]*/;
    --blacka: #0008;
    --blackaa: #0003;
    --white: #fff;
    --whitea: #fff8;
    --whiteaa: #fff2;
}
body {
  background: var(--mainbg) url(/*[[bgimg]]*/) no-repeat fixed center !important;
  cursor: default !important;
  color: var(--mtext);
	}
.poloska_bloka {
    border-bottom: none;
    border-top: 1px solid var(--whiteaa);
    position: relative;
}
body > div.hikinogo_wrapper > div.hikinogo_contiener > div > div.hikinogo_leftblok_contener > div:nth-child(6), body > div.hikinogo_wrapper > div.hikinogo_contiener > div > div.hikinogo_leftblok_contener > div:nth-child(10) {
    border-bottom: 1px solid var(--whiteaa) !important;
}
.hikinogo_header_black, .hikinogo_dmobile {
  display: none !important;    
}
.header44 {
    left: 0;
    background: var(--maina);
    backdrop-filter: blur(10px);
    z-index: 1;
    position: fixed;
    width: 72% !important;
    height: 3.1em;
    margin-top: -43px !important;
    padding: 0 18% 0 14%;
    box-shadow: var(--blacka) 0px 5px 15px;
    overflow: hidden;
}
.horizontal {
    backdrop-filter: blur(10px);
    box-shadow: var(--blacka) 0px 5px 15px;
    background: var(--maina);
    border: none;
}
.menu {
    backdrop-filter: blur(10px);
}
.hikinogo_horizontal2 {
    border: none;
}
.inner a, .horizontal ul li a {
    border: none;
}
.inner img {
    border-radius: 5px !important;
}
.horizontal ul li a:hover {
    border: none;
}
noindex, ins {
  display: none !important;
	}
div[id*=epm] {
  background: none !important;
}
.hikinogo_leftblok1_header, .hikinogo_navigation {
    height: 3em;
}
.hikinogo_deepnavigation {
    top: 0px;
    padding-top: 0px;
}
.hikinogo_topmenu {
    height: 17px;
}
.hikinogo_wrapper {
    top: 2.6em;
    padding-top: 280px;
}
.hikinogo_main_menu > a > img {
    opacity: 0;
    display: inherit;
}
