    margin-top: 0.6em;
}
.header44 .hikinogo_search_panel input {
    padding-left: .8em;
    box-shadow: none;
    border: 1px solid var(--whiteaa);
    border-radius: 1em;
    background: var(--maina);
}
.hikinogo_fbutton2 {
    box-shadow: none;
    border: 1px solid var(--whiteaa);
    border-radius: 1em;
    background: var(--maina);
}
.header44 .hikinogo_search_panel input:focus {
    outline: none;
    border-radius: 1em;
}
table.menu tr td a, table.menu tr td input {
    background: var(--maina);
    border-left: 1px solid var(--maina);
    border-right: 1px solid var(--maina);
    border-top: 1px solid var(--maina);
    text-shadow: 1px 1px var(--maina);
    padding: 6px 20px 0;
}
table.menu tr td a:hover {
    background: var(--mainaa);
}
.hikinogo_leftblok_contener2 {
    border-top: none;
}
.content {
    border-left: 1px solid var(--mainaa);
    background: var(--mainaa);    
}
.hikinogo_contiener {
    background-color: var(--maina) !important; 
    box-shadow: var(--blacka) 0px 5px 15px;
    border: 1px solid var(--whiteaa);
    backdrop-filter: blur(10px);
}
.hikinogo_contener2 {
    border: none;
    background: var(--mainaa) !important; 
}
.hikinogo_quote {
    border: 1px solid var(--whiteaa);
    background: var(--whiteaa);
}
.hikinogo_funct {
    background: transparent;
    border: none;
}
.shortstory {
    border-top: 1px solid var(--whiteaa);
    background: var(--blackaa);
    border-left: 1px solid var(--mainaa);
}
.icons {
    border-bottom: 1px solid var(--mainaa);
    background: var(--blackaa);
    border-left: 1px solid var(--mainaa);
}
.wrapper2 {
    border-radius: 1em;
    border: 3px solid var(--whiteaa);
}
.msupdate_block_date {
    background-color: var(--mainaa);
}
.msupdate_block_list_item {
    border-bottom: 1px solid var(--whiteaa);
    background-color: var(--mainaa);
}
.msupdate_block_list_item:hover {
    background-color: var(--maina);
}
.hikinogo_fbutton-img, span.hikinogo_fbutton-img {
    border-radius: 1em;
    background: var(--maina);
    border: 1px solid var(--whiteaa);
    outline: solid 1px var(--mainaa);
    padding: 4px 7px 0;
}

.fullstory {
    background: var(--mainaa);
    border-left: none;
    border-bottom: 1px solid var(--whiteaa);
    padding: 5px 20px 10px;
}
.multirating-wrapper {
    background: var(--mainaa);
    padding-top: 15px;
    overflow: hidden;
    border-radius: 2px;
    border: 1px solid var(--whiteaa);
    position: relative;
}
.multirating-items-wrapper {
    background: var(--blackaa);
    border-radius: 2px;
    box-shadow: 0 3px 10px rgba(0,0,0,.1);
}
.multirating-item {
    border-bottom: 1px solid var(--blackaa);
}
#dle-content > div:nth-child(1) > div.fullstory > div.fullimg > div.multirating-wrapper.ignore-select > div.multirating-itog {
    border-top: none;
    background: var(--blackaa);
}
.multirating-itog-rateval {
    background: var(--blackaa);
}
.hikinogo_section, .hikinogo_relatednews_title, .relatednews, .ux-form {
    background-color: var(--blackaa);
    border: none;
}
.menu1 > a {
    background: var(--blackaa);
    border-radius: 1em 1em 0 0;
    margin-left: 6px;
}

.hikinogo_icons2 {
    background: var(--blackaa);
    color: #fff;
    position: relative;
    border-left: 1px solid var(--blackaa);
    border-bottom: 1px solid var(--blackaa);
    border-top: 1px solid var(--blackaa);
}
.wrapper3 {
    border: 5px solid var(--blackaa);
}
.f_input, .f_textarea {
    background: var(--blackaa);
    border: 1px solid var(--blackaa);
    color: var(--mtext);
}
.mce-panel {
    background-color: var(--blackaa);
    border: none;
}
.mce-btn-small .mce-ico {
    color: var(--mtext);
}
.hikinogo_fbutton, span.hikinogo_fbutton {
    text-decoration: none;
    color: var(--mtext);
    background: var(--blacka);
    border: 1px solid var(--whiteaa);
    border-radius: 1em;
    outline: none;
}
.hikinogo_fbutton:hover, .hikinogo_fbutton:focus, .hikinogo_fbutton-img:hover, .hikinogo_fbutton:focus, .hikinogo_fbutton2:hover, .hikinogo_fbutton2:focus, .fbutton4:hover, .hikinogo_fbutton:focus, .fbutton5:hover, .fbutton5:focus {
    background-color: var(--blackaa);
    border-radius: 1em;
    border: 1px solid var(--whiteaa);
}
.hikinogo_mimiserblock {
    border-top: none;
}
.menu1 > div:nth-of-type(1), .menu1 #tab2:target ~ div:nth-of-type(2), .menu1 #tab3:target ~ div:nth-of-type(3) {
    display: block;
    border: none;
}
}
