style.css:
@-moz-document url-prefix("https://tabun.everypony.ru") {

body,
aside section,
#header,
#widemode,
#c-header,
#container,
.topic-content.text,
.comment-content,
.comment-info {
  background: #2b2b2b;
  color: #a9b7c6 !important;
}

h1, h2, h3, h4, h5, h6 {
  color: #a9b7c6 !important;
}

a {
  transition: color 0.3s;
}
a, a:visited {
  color: #287bde !important;
}
a:hover {
  color: #5394ec !important;
}

img {
  transition: opacity 0.3s;
}
img {
  opacity: 0.7;
}
img:hover {
  opacity: 1.0;
}

blockquote {
  color: #a9b7c6 !important;
  background: #3c3f41 !important;
  border: 1px dashed #888a85 !important;
  border-left: 5px solid #87929e !important;
}

pre, ul, li {
  background: inherit !important;
}

li.active a, li.active span, li.active article.topic .topic-info-comments span.count, article.topic .topic-info-comments li.active span.count {
  color: #cc7832 !important;
}

nav a, .nav a {
  text-decoration: none;
}

textarea, input {
  color: #a9b7c6 !important;
  background-color: inherit !important;
}

button {
  border-radius: 0.4em;
  color: #a9b7c6 !important;
}
button.button {
  background: #3c3f41;
  border-color: #555753 !important;
}
button.button-primary.active, button.button-primary:hover, button.button-action.active, button.button-action:hover {
  background: #204a87 !important;
  border-color: #3465a4 !important;
}

table tr td, table tr th {
  border-color: #555753 !important;
}
table tr:hover td {
  background: #3c3f41 !important;
}
table tr .cell-tab-inner.active {
  background: #313335 !important;
  border-color: #3c3f41 !important;
}

aside a, aside a:visited {
  color: #87929e !important;
}
aside a.author, aside a.author:visited {
  color: #287bde !important;
}
aside span.block-item-comments, aside article.topic .topic-info-comments span.block-item-comments.count, article.topic .topic-info-comments aside span.block-item-comments.count {
  color: #4e9a06 !important;
}
aside section, aside #block_blogs {
  background: #313335 !important;
  border-color: #3c3f41 !important;
  border-radius: 0.4em;
  border-bottom: none !important;
  margin: 1em;
}
aside footer {
  background: inherit !important;
}

.comment {
  background: none !important;
}
.comment .comment-content {
  background: #313335 !important;
  border-color: #3c3f41 !important;
  border-radius: 0.4em;
}
.comment .folding {
  background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABoAAAANAgMAAADK95Z +AAAACVBMVEUAAAA8P0Gpt8b/KAcMAAAAAXRSTlMAQObYZgAAADBJREFUCNdjYA0NYQwNZWAQYGBkYW BwAEKGACAPTDNhoZ1WLQhYtQKnPEw/zDyY+QBiQAunMlp2oAAAAABJRU5ErkJggg==") 0 0;
}
.comment .folding.folded {
  background-position: -13px 0;
}
.comment .comment-info li a.reply-link {
  text-decoration: underline !important;
  border: none;
}
.comment .comment-info li.comment-hide a {
  color: #87929e !important;
}
.comment.comment-new .comment-content {
  background: #2e3633 !important;
}
.comment.comment-self .comment-content {
  background: #473c44 !important;
  border: #594f56;
}
.comment.comment-current .comment-content {
  background: #3c4447 !important;
  border-color: #4f5659;
}

.comment-author a {
  color: #a9b7c6 !important;
  text-decoration: none;
}

.comment-preview {
  background: #555753 !important;
  border-color: #888a85;
  border-radius: 0.4em;
}

aside div.search-form-wrapper {
  background: #3c3f41;
  border-color: #555753 !important;
}

ul.stream-list .stream-comment-preview {
  box-shadow: 0 2px 4px #555753 inset;
  color: #a9b7c6 !important;
  background: #3c3f41;
  border-color: #555753 !important;
}
ul.stream-list .stream-comment-preview::after {
  border-bottom-color: #555753;
}

article.topic, article.topic div:not(.vote-item) {
  background: #313335 !important;
  border-color: #3c3f41 !important;
  border-radius: 0.4em;
}
article.topic .topic-header, article.topic .topic-content, article.topic .topic-footer {
  padding: 0.6em;
}
article.topic a[title="Читать дальше"] {
  background: #2b2b2b;
  text-decoration: none;
  font-weight: bold;
  border: 1px dashed #888a85;
  color: #cc7832 !important;
}
article.topic .topic-info-comments a {
  text-decoration: none;
}
article.topic .topic-info-comments span, article.topic .topic-info-comments span.count {
  text-decoration: none !important;
  color: #246e00 !important;
}
article.topic .topic-info-comments span.count {
  color: #cc7832 !important;
}
article.topic .poll dd div {
  transition: background 0.3s;
}
article.topic .poll dd div {
  background: #555753 !important;
}
article.topic .poll dd div:hover {
  background: #888a85 !important;
}

#blog-mini {
  background: #3c3f41;
  border-color: #555753 !important;
}

#blog, #blog footer {
  background: #3c3f41;
  border-color: #555753 !important;
}
#blog .blog-info li span, #blog .blog-info li strong {
  background: none !important;
}
#blog .user-avatar a, #blog .admin a {
  color: #cc7832 !important;
  text-decoration: none;
}

.profile-dotted-list li span, .profile-dotted-list li article.topic .topic-info-comments span.count, article.topic .topic-info-comments .profile-dotted-list li span.count, .profile-dotted-list li strong {
  background: inherit !important;
}

.profile-info-about {
  color: #a9b7c6 !important;
}

.block-type-profile-nav li {
  border-bottom: none !important;
}
.block-type-profile-nav li a {
  color: #287bde !important;
  text-decoration: none !important;
}

section.block-type-profile {
  background: inherit !important;
}

.form-profile .wrapper-content, .upload-photo, .profile-page-header {
  background: #313335 !important;
  border-color: #3c3f41 !important;
}

.talk-search, #block_talk_search_content, .talk-blacklist-form {
  background: #313335 !important;
  border-color: #3c3f41 !important;
}
.talk-search header, #block_talk_search_content header, .talk-blacklist-form header {
  border: none !important;
}
.talk-search .talk-search-content, #block_talk_search_content .talk-search-content, .talk-blacklist-form .talk-search-content {
  padding-top: 0;
  border: none;
}

.spoiler-title {
  background: #3c3f41;
  border-color: #555753 !important;
}

.comment-current .comment-content .spoiler-title {
  background: #313335 !important;
  border-color: #3c3f41 !important;
}

.spoiler-gray {
  background: #555753;
  color: #555753;
}
.spoiler-gray:hover {
  color: inherit;
}

.markItUpHeader {
  background: #3c3f41;
  border-color: #555753 !important;
}

@-webkit-keyframes spin {
  0% {
    -webkit-transform: rotate(0deg);
    transform: rotate(0deg);
  }

  100% {
    -webkit-transform: rotate(359deg);
    transform: rotate(359deg);
  }
}

@keyframes spin {
  0% {
    -webkit-transform: rotate(0deg);
    transform: rotate(0deg);
  }

  100% {
    -webkit-transform: rotate(359deg);
    transform: rotate(359deg);
  }
}

