style.css:
@-moz-document domain("open2ch.net") {
/* 削除依頼ボタンを消す */
div[style="padding:2px;margin:2px;background:#AAAAAA;color:white"] {
display:none !important;
}
/* komeヘッダを削除 */
div#kheader {
display:none !important;
}
/* 使い方|ヘッドラインリンクを削除 */
div#kmain div[align="right"] {
display:none !important;
}
/* komeの全体サイズ変更 */
div#komediv {
position:absolute;
top:0px !important;
right:0px !important;
bottom:0px !important;
width:300px !important;
}
/* 入力欄を下に移動＋半透明化 */
div#komediv div[style="background:#ddffdd;padding:3px;font-size:9pt;border:1pt solid #333"] {
position:absolute;
bottom:0px !important;
right:0px !important;
opacity:0.75 !important;
}
/* ログを広げる＋横スクロール表示 */
div#klog_view {
position:absolute;
top:300px !important;
right:0px !important;
left:0px !important;
bottom:110px !important;
width:auto !important;
height:auto !important;
overflow:scroll !important;
}
a[href^="https://www.facebook.com"]{display:none !important;}/* Facebookリンクボタンを消す */
a[href^="https://twitter.com"]{display:none !important;}/* Twitterリンクボタンを消す */
a[href^="http://b.hatena.ne.jp"]{display:none !important;}/* hatenaリンクボタンを消す */
.social {display:none !important;}/* Twitterとかのボタンを消す */
.copyrights {display:none !important;}/*投稿は著作権をうんぬん書いてあるのを消す */
a[href="http://satoru.net"]{display:none !important;}/* satoru.netのリンクを消す*/
}
