style.css:
@-moz-document url-prefix("https://www.tumblr.com") {

#new_post {
  background-image:
    url('/*[[background]]*/'),
    url('http://data.sainet.co.uk/medigel/background/general/b99d3d.jpg');
  background-position: top center;
  background-repeat: no-repeat, repeat;
}

/*#posts .post.new_post .arrow {
background: transparent url('http://data.sainet.co.uk/medigel/nipple/nipple_harrypotter_1.png') !important;
background-repeat: no-repeat !important;
}*/

.new_post_label_text {
  line-height: 2.5em;
  color: rgba(50,33,6,0.65);
  text-shadow: 1px 1px 0px rgba(255,255,255,0.3);
  font-family: Book Antiqua, Georgia, Times New Roman, Serif;
  letter-spacing: 0.05em;
  text-transform: uppercase;
  font-weight: bold;
}

#new_post .main_content {
background: rgba(255,255,255,0.95) !important;
color: rgba(0,0,0,0.7) !important;
border: none !important;
}

#new_post .popover_inner.clearfix, #new_post .post_options .popover_inner, #tag_suggest.popover, .post_options.popover {
background: rgba(255,255,255,0.95) !important;
}

#new_post #post_header {
  padding-top: 30px;
}

#new_post #post_header .txt {
  color: rgba(50,33,6,0.55);
  text-shadow: 1px 1px 0px rgba(255,255,255,0.3);
  font-family: Book Antiqua, Georgia, Times New Roman, Serif;
  letter-spacing: 0.05em;
  text-transform: uppercase;
  margin: 0 0 -8px 0;
}

#new_post .cog {
  padding-top: 30px;
  background-position: -40px 18px;
  background-image: url('http://data.sainet.co.uk/medigel/general/ajax_post_forms_sprite.png');
  opacity: .7;
}

a.new_post_label > i {
  background: url('/*[[icons]]*/') no-repeat;
  transition: background-position .08s linear !important;
  margin-top: 12px;
}
a.new_post_label > i:before {
  content: none;
}
#new_post_label_text > I {background-position: 2px -8px}
#new_post_label_photo > I {background-position: -73px -8px}
#new_post_label_quote > I {background-position: -148px -8px}
#new_post_label_link > I {background-position: -223px  -8px}
#new_post_label_chat > I {background-position: -298px -8px}
#new_post_label_audio > I {background-position: -373px -8px}
#new_post_label_video > I {background-position: -448px  -8px}

#new_post_label_text:hover > I {background-position: 2px -12px}
#new_post_label_photo:hover > I {background-position: -73px -12px}
#new_post_label_quote:hover > I {background-position: -148px -12px}
#new_post_label_link:hover > I {background-position: -223px  -12px}
#new_post_label_chat:hover > I {background-position: -298px -12px}
#new_post_label_audio:hover > I {background-position: -373px  -12px}
#new_post_label_video:hover > I {background-position: -448px  -12px}

#new_post_label_text:active > I {background-position: 2px -8px}
#new_post_label_photo:active > I {background-position: -73px -8px}
#new_post_label_quote:active > I {background-position: -148px -8px}
#new_post_label_link:active > I {background-position: -223px -8px}
#new_post_label_chat:active > I {background-position: -298px -8px}
#new_post_label_audio:active > I {background-position: -373px -8px}
#new_post_label_video:active > I {background-position: -448px -8px}

}
