style.css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us"), domain("replay.pokemonshowdown.com") {
/* Change the background URL in this section to add your own image next to your name! Image should be 32x32. */

.userbar:before{
  display: block;
  content:"";
        width:32px;
        height: 32px;
        float: left;
        position: relative;
        top: -9px; background: url(http://i.imgur.com/6OEFrZC.png);
      }

@font-face {
font-family: "bebas neue";
src: url("https://dl.dropbox.com/s/oz9ujl10j4ixziw/BEBASNEUE.OTF");
font-weight:bold;
}

@font-face {
font-family: "Russo"; font-style: normal;
  font-weight: 800;
  src: local('Exo ExtraBold'), local('Exo-ExtraBold'), url(http://fonts.gstatic.com/s/exo/v4/bxee2kBKNXMDa_dqwEq7Bg.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;

}


@font-face {
font-family: "bank gothic";
font-style: normal;
 src: local('bank gothic'),  url(https://dl.dropbox.com/s/aayl8qeixmgs2as/Bank%20Gothic%20Medium-webfont.woff2) format('woff2');
}



     
.icon-user{
     display:none; 
    }

/*********************************************************
 * Global
 *********************************************************/

a:link {
    color: #30a4e0;
}


a:visited {
    color: #8f30e0;
}
html, body {
	margin: 0;
	padding: 0;
}
body {
	background:url(http://i.imgur.com/wlHm7B4.png) !important;
	color: white;
	font-size: 11pt;
	font-family: Verdana, Helvetica, Arial, sans-serif;
}

.optlabel {
	font-size: 9pt;
	display: block;
}
.textbox {
	border: 1px solid #AAAAAA;
	border-radius: 3px;
	padding: 2px 3px;
	font-family: Verdana, Helvetica, Arial, sans-serif;
	font-size: 9pt;

	box-shadow: inset 0px 1px 2px #CCCCCC, 1px 1px 0 rgba(255,255,255,.6);
	background: #F8FBFD;
	color: black;
}
.textbox:hover {
	border-color: #474747;
	box-shadow: inset 0px 1px 2px #D2D2D2, 1px 1px 0 rgba(255,255,255,.6);
	background: #FFFFFF;
}
.textbox:focus {
	outline: 0 none;
	border: 1px solid #004488;
	box-shadow: inset 0px 1px 2px #D2D2D2, 0px 0px 5px #2266AA;
	background: #FFFFFF;
}
a.subtle {
	text-decoration: none;
	color: black;
}
a.subtle:hover {
	text-decoration: underline;
	color: #6688CC;
}
.buttonbar {
	margin-top: 1em;
	text-align: center;
}

/*********************************************************
 * Header
 *********************************************************/

i.fa.fa-user {display:none;}

.header {
	position: relative;
	height: 50px;
  	background: url(http://i.imgur.com/hWvT3tA.png);
}
.userbar .username {
	color: black;
	text-shadow: none;
}
.userbar button.icon i {
	margin: 0 -5px;
}
.tabbar {
	position: absolute;
	bottom: 0px;
	left: 0px;
	right: 0;

	display: block;
	list-style: none;
	margin: 0;
	padding: 0 0 0 0;
	height: 35px;
	text-align: left;

	color: white;
	text-shadow: 0 1px 0 black;
	font-size: 10pt;

	line-height: 100%;
}
.maintabbarbottom {
	content: "";
	display: block;
	position: absolute;
	left: 0;
	right: 0;
	bottom: -2px;
	height: 2px;
	background: #c2221f;
	border: solid 0px #AAAAAA;
	border-left: 0;
	margin: -1px 0 0 0;
	-webkit-box-shadow: 0 1px 2px rgba(0,0,0,.2);
	-moz-box-shadow: 0 1px 2px rgba(0,0,0,.2);
	box-shadow: 0 1px 2px rgba(0,0,0,.2);
}
.tabbar.maintabbar {
	margin-left: 144px;
	margin-right: 165px;
	overflow: hidden;
}

.tabbar li,
.tabbar ul {
	display: block;
	float: left;

	list-style: none;
	margin: 0;
	padding: 0;  
}
.tabbar a.button {
	outline: none;
	display: inline-block;
	height: 28px;
	min-width: 52px;
	cursor: pointer;
	text-align: center;
	text-decoration: none;
	text-shadow: 0 1px 0 rgba(255,255,255,.4);
	border-radius: 0px;
	-webkit-box-shadow: 0 1px 2px rgba(0,0,0,.2), inset 0 -1px 2px rgba(255,255,255,1);
	-moz-box-shadow: 0 1px 2px rgba(0,0,0,.2), inset 0 -1px 2px rgba(255,255,255,1);
	box-shadow: 0 1px 2px rgba(0,0,0,.2), inset 0 -1px 2px rgba(255,255,255,1);
	white-space: nowrap;
  	transform:skewX(-30deg);
	/* default colors */
	color: #707070;
	text-shadow: 0 0px 0 #707070;
	border: solid 2px #860a07;
  border-left-width: 3px;
  border-right-width: 3px;
	background-image: url("http://i.imgur.com/Lr9P13M.png");

	position: relative;
	padding: 3px 2px 3px 2px;
	margin: 0 -1px 0 0;
	top: 1px;
	border-radius: 0;
	box-shadow: none;
	font-size: 11px;
}
.tabbar a.button i {
	display: block;
	text-align: center;
	margin: 1px auto 0 auto;
	font-style: normal;
	font-size: 14px;
	height: 14px;
  	transform:skewX(30deg);
}
.tabbar a.button span {
	display: block;
	overflow: hidden;
  	transform:skewX(30deg);
}
.tabbar a.button i.text {
	font-size: 11px;
	margin-right: 8px;
	padding-left: 0;
	overflow: hidden;
	font-weight: bold;
  	transform:skewX(30deg);
}
.tabbar a.button i.text + span {
	margin-right: -6px;
}
.tabbar a.button:hover{
  	color:#f8f8f8;
	background-image: url("http://i.imgur.com/Lr9P13M.png");
	border-color: #BB2222;
}
.tabbar a.button:active {
  	color:#f8f8f8;
	background-image: url("http://i.imgur.com/Lr9P13M.png");
	border-color: #BB2222;

}
.tabbar a.button.notifying {
	border-color: #860a07;
	background: url("http://i.imgur.com/t8yebRQ.png");
}
.tabbar a.button.notifying:hover {
	border-color: #bb2222;
	background: url("http://i.imgur.com/t8yebRQ.png");
}
.tabbar a.button.cur,
.tabbar a.button.cur:hover {
	color: #f8f8f8;
	text-shadow: 0 1px 0 #707070;
	background-image: url("http://i.imgur.com/Lr9P13M.png");
	box-shadow: none;
	border-color: #BB2222;
	border-bottom: 0;
	cursor: default;
	padding-top: 3px;
	padding-bottom: 5px;
	top: 0px;
}
.tabbar li:first-child a.button {
	border-top-left-radius: 5px;
	margin-left: 9px;
}
.tabbar li:last-child a.button {
	border-top-right-radius: 5px;
}
.tabbar a.closable {
	padding-right: 8px;
}
.tabbar .closebutton {
	display: inline-block;
	margin: 0 0 0 -18px;
	width: 18px;
	position: relative;
	z-index: 11;
	top: 3px;
	vertical-align: top;
  	color:#707070;
}
.tabbar a.cur + .closebutton {
	top: 3px;
}
.closebutton,
.minimizebutton {
	text-decoration: none;
	font-size: 14px;
	color: #999999;
	text-shadow: none;
	border: 0;
	padding: 0;
	margin: 0;
	background: transparent;
	cursor: pointer;
  	left:3px;
  	bottom:2px;
}
.closebutton:hover,
.minimizebutton:hover {
	color: #BB2222;
    border:none !important;
    background:none !important;
}
.closebutton:active,
.minimizebutton:active {
	color: #661111;
    border:none !important;
    background:none !important;
}

/*********************************************************
 * Rooms
 *********************************************************/

.ps-room {
	position: absolute;
	display: block;
	top: 52px;
	left: 0;
	right: 0;
	bottom: 0;
	border-left: 2px solid #c2221f;
	margin-left: -2px;
}
.scrollable {
	overflow: auto;
	-webkit-overflow-scrolling: touch;
	overflow-scrolling: touch;
}
.ps-room.ps-room-light {
	background: #282828;
	color: #aaa;
}
.ps-room.ps-room-opaque {
	background: url("http://i.imgur.com/SWQeVpB.png");
  	font-size:18px;
	color: #CCCCCC;
  	font-family:"bebas neue" !important;
}

.ps-overlay {
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	background: #CCCCCC;
	background: rgba(212, 212, 212, .5);

	overflow: auto;
	-webkit-overflow-scrolling: touch;
	overflow-scrolling: touch;
	z-index: 20;
}


.ps-popup {
	position: absolute;
	top: auto; right: auto; left: auto; bottom: auto;
	text-align: left;
	background: #282828;
	color: #aaa;
	border: 1px solid #bb2222;
	padding: 2px 10px;
	border-radius: 0px;
	box-shadow: none;
	z-index: 20;
}


.ps-popup a:link {
    color: #146dc8;
}


.ps-popup a:visited {
    color: #7c1cce;
}
.ps-overlay .ps-popup {
	position: static;
	margin: 80px auto 20px auto;
	max-width: 320px;
}
.ps-popup p,
.ps-popup h3 {
	margin: 7px 0;
}
.ps-popup h3 {
	font-size: 12pt;
}
p.or {
	font-size: 12pt;
	font-style: italic;
	font-weight: bold;
	color: #444444;
	margin: 20px 0;
	text-align: center;
}
p.or:before,
p.or:after {
	content: "";
	font-size: 10px;
	padding: 0 0 0 85px;
	margin: 0 9px 0 0;
	border-bottom: 1px solid #777777;
	position: relative;
	top: -7px;
}
p.or:after {
	margin: 0 0 0 9px;
}
.ps-popup p.error {
	font-weight: bold;
}

.popupmenu, popupmenu li {
	margin: -2px 0 -1px 0;
	padding: 0;
	list-style: none;
	font-size: 8pt;
}
.popupmenu h3 {
	margin: 5px 0 0 -2px;
	padding: 1px 0;
	font-size: 8pt;
	color: #fff;
}
.popupmenu li:first-child h3 {
	margin-top: 0;
}
.popupmenu button {
	display: block;
	cursor: pointer;
	font-size: 8pt;
	font-family: Verdana, Helvetica, Arial, sans-serif;
	margin: 0 0 0 6px;
	padding: 0 4px;
	border: 0;
	border-radius: 0px;
	background: transparent;
	color: #aaa;
	width: 204px;
	height: 19px;
	text-align: left;

	box-sizing: border-box;
}

@media (max-height:590px) {
	.popupmenu button {
		height: 17px;
	}
	.popupmenu h3 {
		margin-top: 2px;
	}
	.popupmenu, popupmenu li {
		margin: -3px 0 -2px 0;
	}
}
@media (max-height:518px) {
	.popupmenu button {
		height: 16px;
	}
	.popupmenu h3 {
		margin-top: 1px;
	}
}

.popupmenu button.sel {
	padding: 0 3px;
	border: 1px solid #AAAAAA;
}
.popupmenu button:hover,
.popupmenu button.sel:hover {
	border-radius: 0px;
	padding: 0 3px;
	border: 1px solid #888888;
	background: rgba(0,0,0,0.3);
}
.select {
	cursor: pointer;
	box-sizing: border-box;
	width: 230px;
	height: 32px;
	padding: 0 5px;
	font-size: 9pt;
	text-align: left;
	font-family: Verdana, Helvetica, Arial, sans-serif;

	border-radius: 0px;
	border: 2px solid #282828;
	box-shadow: inset 0 1px 0 #FFFFFF;
	background: url("http://i.imgur.com/cON9TA0.png");
	color: #aaa;
}
.select:hover {
  	color:#fff;
	border-radius: 0px;
	border: 2px solid #282828;
	background: url("http://i.imgur.com/Q5an6Ul.png");
    
}
.select:active {
	border: 2px solid #282828;
	border-radius: 0px;
    outline:pink;
	background: url("http://i.imgur.com/Q5an6Ul.png");
	box-shadow: inset 0 1px 0 #FFFFFF;
}
.select:after {
	float: right;
	margin-top: 1px;
	font-size: 14px;
	content: "\f0d7";
  font-family: FontAwesome;
  -webkit-font-smoothing: antialiased;
  color: #777777;
}
.select:disabled {
	border-color: #999999;
	background: rgba(220,220,220,.3);
	color: #555555;
	box-shadow: none;
}
.select:disabled:after {
	content: "";
}
.select.preselected,
.select:disabled.preselected {
	color: black;
}

/*********************************************************
 * Main menu
 *********************************************************/

.leftmenu {
	padding: 40px 0 0 0;
	width: 324px;
}
.rightmenu {
	position: absolute;
	top: 40px;
	right: 25px;
}
.activitymenu {
	position: absolute;
	left: 325px;
	top: 40px;
}
.mainmenu {
	padding: 0 0 30px 0;
}

.battleform {
	width: 230px;
	text-align: left;
	margin: 0 auto;
}
.battleform .buttonbar {
	text-align: center;
}

.mainmenuwrapper {
	position: relative;
	min-height: 100%;
}
.mainmenufooter {
	position: absolute;
	bottom: 15px;
	left: 20px;
	color: #BBBBBB;
	text-shadow: 0 0 1px black, 0 0 1px black, 0 0 1px black;
}
.mainmenufooter blockquote {
	margin: 0 0 1em 0;
	padding: 0;
}
.mainmenufooter small {
	font-size: 9pt;
}
.mainmenufooter a {
	text-decoration: underline;
	color: #6688CC;
}
.mainmenufooter a:hover {
	text-decoration: underline;
	color: #99BBFF;
}

.pmbox {
	width: 270px;
	margin: 0 auto;
	min-height: 2px;
}
.pm-window {
	margin-bottom: 12px;
}
.pm-window h3 {
	background: #4b4b4b;
	color: #aaa;
	margin: 0;
	padding: 2px 5px;
	font-size: 11pt;
	border: 2px solid #4b4b4b;
	border-top-left-radius: 0px;
	border-top-right-radius: 0px;
	cursor: pointer;
}
.pm-window h3:hover {
	color: #333333;
}
.pm-window h3.pm-minimized {
	border-radius: 5px;
}
.pm-window h3.pm-notifying {
	border-color: #AA8866;
	background: #E3C3A3;
}
.pm-window h3.pm-notifying:hover {
	border-color: #604020;
	background: #CFAF8F;
}
.pm-window h3 .closebutton,
.pm-window h3 .minimizebutton {
	float: right;
	padding-right: 1px;
}
.pm-window h3 small {
	color: #888888;
	font-weight: normal;
	vertical-align: 1px;
}
.pm-log {
	min-height: 100px;
	max-height: 300px;
	background: #282828;
	color: #aaa;
	word-wrap: break-word;
	border: 2px solid #4b4b4b;
	border-top: 0;
	border-bottom: 0;

	overflow: auto;
	-webkit-overflow-scrolling: touch;
	overflow-scrolling: touch;
}
.pm-log-add {
	border: 0px solid #c2221f;
	border-bottom-left-radius: 0px;
	border-bottom-right-radius: 0px;
	background: #4b4b4b;
}
.pm-window.collapsed .pm-log,
.pm-window.collapsed .pm-log-add {
	display: none;
}
.pm-window.focused h3,
.pm-window.focused .pm-log,
.pm-window.focused .pm-log-add {
	border-color: #555555;
}
.pm-window.focused h3,
.pm-window.focused h3:hover {
	background: #bebebe;
	color: #222222;
	cursor: default;
}
.challenge {
	margin-top: -1px;
	background: #282828;
	border: 1px solid #f57b21;
	color: #fff;
	padding: 1px 5px;
}
.challenge p {
	margin: 4px 0;
}

.tiny-layout .leftmenu,
.tiny-layout .rightmenu,
.tiny-layout .activitymenu {
	position: static;
	top: 0;
	right: 0;
	left: 0;
	width: auto;
	margin: 0;
	padding: 0;
}
.tiny-layout .leftmenu {
	padding-top: 30px;
}
.tiny-layout .activitymenu {
	padding-bottom: 8px;
}

.mainmessage {
	display: block;
	padding: 10px 15px;
	margin: 30px 40px;
	max-width: 400px;
	border-radius: 20px;

	background: rgba(201, 207, 219, .8);
	color: black;
}
.menugroup {
	display: block;
	padding: 10px 0;
	margin: 0 27px 30px 27px;
	max-width: 400px;
	border-radius: 20px;
	color: white;
	text-shadow: 0 1px 0 black;
}
.menugroup {
	background: #4b4b4b;
	text-align: center;
}

.mainmessage p,
.menugroup p {
	margin: 10px 0;
}
.mainmenu .menugroup:nth-child(2) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button, body .button.mainmenu4, body .button.mainmenu5 {
	box-sizing: border-box;
	font-size: 12pt;
	font-family: Verdana, Helvetica, Arial, sans-serif;

	display: inline-block;
	padding: 5px 7px 3px 7px;

	width: 200px;
	box-shadow:none;
	outline: none;
	cursor: pointer;
	text-align: center;
	text-decoration: none;
	text-shadow: 0 -1px 0 hsl(210,40%,10%);
	border-radius: 5px;

	/* default colors */
	color: #aaa;
	border: solid 2px #860a07;
	background: #779EC5;
	background: url("http://i.imgur.com/KpRUrnS.png");
	box-shadow:none;
	outline: none;
	cursor: pointer;
	text-align: center;
	text-decoration: none;
	text-shadow: 0 -1px 0 hsl(210,40%,10%);
	border-radius: 5px;
}
 .mainmenu .menugroup:nth-child(1) .button {
	width: 230px;
	padding: 6px 7px 6px 7px;
	font-size: 14pt;
	color: #aaa;
	border: solid 2px #860a07;
	background: #779EC5;
	background: url("http://i.imgur.com/KpRUrnS.png");
	box-shadow:none;
	outline: none;
	cursor: pointer;
	text-align: center;
	text-decoration: none;
	text-shadow: 0 -1px 0 hsl(210,40%,10%);
	border-radius: 5px;
}
  .mainmenu .menugroup:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover, body .button.mainmenu4:hover, body .button.mainmenu5:hover {
	background: url("http://i.imgur.com/XxM6iVF.png");
	border-color: #bb2222;
}
 .mainmenu .menugroup:nth-child(1) .button:active,.mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:active,.mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:active,.mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:active,.mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:active,.mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:active, body .button.mainmenu4:active, body .button.mainmenu5:active {
	box-shadow: none;
	background: url("http://i.imgur.com/KpRUrnS.png") !important;
	border-color: #bb2222;
}


.menugroup .button.disabled,
.menugroup .button.disabled:hover,
.menugroup .button.disabled:active {
	cursor: default;
	background: #EEEEEE;
	border: 1px solid #CCCCCC;
	color: #999999;
	text-shadow: none;
	box-shadow: 0 1px 2px rgba(0,0,0,.1);
}

.rightmenu {
	width: 294px;
}
.rightmenu .menugroup .button {
	width: 180px;
}
@media (max-width:895px) {
	.mainmenu {
		padding-bottom: 1px;
	}
	.rightmenu {
		position: static;
		top: 0;
		right: 0;
		padding-bottom: 30px;
	}
	.rightmenu {
		width: 324px;
	}
	.rightmenu .menugroup .button {
		width: 200px;
	}
}

/*********************************************************
 * Ladder
 *********************************************************/

.ladder table, .ladder td, .ladder th {
	border-collapse: collapse;
	border: 1px solid #BBBBBB;
}
.ladder td, .ladder th {
	padding: 3px 5px;
}
.ladder th {
	text-align: left;
	font-size: 9pt;
	background: #EEEEEE;
}
.ladder span {
	color: gray;
}

.roomcounters button {
    display: block;
	border: 2px solid #860a07;
	background: url("http://i.imgur.com/kW45q0f.png");
	border-radius: 4px;
	text-decoration: none;
	color: #aaa;
	text-shadow: none;
	box-shadow: none;
	cursor: pointer;
	font-size: 10pt;
}

.roomcounters button:hover {

	border: 2px solid #c2221f;
	background: url("http://i.imgur.com/LucasN4.png");
	color: #c4c4c4;
	text-decoration: none;
}

.roomcounters button:active {
	box-shadow: none;
	background: url("http://i.imgur.com/KpRUrnS.png") !important;
	border-color: #bb2222;
}

.roomlist {
	text-align: left;
  	background:#282828;
}
.roomlist a.ilink {
	display: block;
	margin: 2px 7px 4px 7px;
	padding: 1px 4px 2px 4px;
