	padding: 1px;
	height: 6px;
  	border: solid 1px black;
	margin: 0 0 0 0;
	width: 151px;
	border-radius: 0px;
}

/* The declaration order of these hp* classes is significant. */

.statbar .hpbar .hp
{
	height: 2px;
	border-top: 2px solid #00dd60;
