/*SWITCH MENU*/
.movemenu button {
  	top:0px;
    right:100px;
	position: relative;
	outline: none;
	cursor: pointer;
	text-align: center;
	text-decoration: none;
	text-shadow: 0 1px 0 rgba(255,255,255,0);
	border-radius: 0px;

	/* default colors */
	color: white;
	background: #ff66cc;
	background: -webkit-gradient(linear, left top, left bottom, from(#f6f6f6), to(#e3e3e3));
	background: -moz-linear-gradient(top,  #f6f6f6,  #e3e3e3);
	background: linear-gradient(top,  #f6f6f6,  #e3e3e3);
}
.switchmenu button:hover{
	background: #cfcfcf;
  background: -webkit-gradient(linear, left top, right top, color-stop(0%,rgba(103,103,103,0.4)),   color-stop(100%,rgba(0,0,0,0))) !important;
	background: -moz-linear-gradient(top,  #f2f2f2,  #cfcfcf);
	background: linear-gradient(top,  #f2f2f2,  #cfcfcf);
	border-color: #606060;
    border:none !important;
    color:white!important;
}
.switchmenu button:active {
	 background: -webkit-gradient(linear, left top, right top, color-stop(0%,rgba(197,197,197,0.4)),   color-stop(100%,rgba(0,0,0,0))) !important;

	background: -moz-linear-gradient(top,  #cfcfcf,  #f2f2f2);
	background: linear-gradient(top,  #cfcfcf,  #f2f2f2);
    border:none !important;
    color:white !important;
}

.switchmenu button .disabled,
.switchmenu button:disabled,
.movemenu button:disabled {
	cursor: default;
  background: -webkit-gradient(linear, left top, right top, color-stop(0%,rgba(0,0,0,0)),   color-stop(100%,rgba(0,0,0,0))) !important;
	border-color: #860a07 !important;
	-webkit-box-shadow: none !important;
	-moz-box-shadow: none !important;
	box-shadow: none !important;
	color: #777777 !important;
}

