style.css:
/* LAYOUT GENERAL */

@-moz-document url-prefix("http://www.jeuxvideo.com/") {
#header-top, #jv-footer .liens-pied {
	background : #2c2c2c;
}

#header-bottom, #header-bottom .bloc-sticker{
	background : #434343;
}

#header-store-jv #header-store-jv-link{
	background-color : #2D363C;
}

#prospect-interstice, #zone-sponso, #prospect {
	display : none;
}
}

/* FORUMS ET TOPICS */

@-moz-document url-prefix("http://www.jeuxvideo.com/forums/"), url-prefix("http://www.jeuxvideo.com/recherche/forums/") {
#content {
    background-color: #4d4d4d;
    background-image: none;
}

.container-content {
    background-color: #5f5f5f;
	margin-top : 0;
}

a, .lien-jv {
    color: #f5e480;
}

a:hover, .lien-jv:hover {
    color: #739dfe;
}

.titre-bloc-forum span {
    color: #ffffff !important;
}

.bloc-fil-ariane-crumb .fil-ariane-crumb a, .bloc-fil-ariane-crumb-forum .fil-ariane-crumb a {
	color : #f5e480;
	font-weight : bold;
	font-size : 1.1em;
}

.bloc-fil-ariane-crumb .fil-ariane-crumb a:hover, .bloc-fil-ariane-crumb-forum .fil-ariane-crumb a:hover {
	color : #739dfe;
}

.bloc-fil-ariane-crumb .fil-ariane-crumb .highlight, .bloc-fil-ariane-crumb-forum .fil-ariane-crumb .highlight {
	color : #c9ac00;
	font-weight : bold;
	font-size : 1.1em;
}

#table-liste-topic-forum {
    background-color: #5f5f5f;
    border: 1px solid #444444;
}
  
#table-liste-topic-forum tr, #table-liste-topic-forum tr .sujet-topic a {
    color: #dddddd !important;
}
  
#table-liste-topic-forum .sujet-topic a:visited {
    color: #999999 !important;
}

a.xXx.text-user, #table-liste-topic-forum .auteur-topic a.xXx.text-user {
	color : #fff;
}

a.xXx.text-admin, #table-liste-topic-forum .auteur-topic a.xXx.text-admin {
	color : #f43434;
}

a.xXx.text-redac, #table-liste-topic-forum .auteur-topic a.xXx.text-redac {
	color : #28aa17;
}

a.xXx.text-modo , #table-liste-topic-forum .auteur-topic a.xXx.text-modo {
	color : #f1882b;
}

a.xXx.text-expert , #table-liste-topic-forum .auteur-topic a.xXx.text-expert {
	color : #6196e6;
}

#table-liste-topic-forum tbody tr, .bloc-message-forum:nth-child(even) {
    background-color: #404040;
}

#table-liste-topic-forum tbody tr:nth-child(2n+1), .bloc-message-forum:nth-child(odd) {
    background-color: #494949;
}
    
#table-liste-topic-forum tbody tr:hover {
    background-color: #555555;
}

.titre-head-bloc .titre-bloc, #bloc-title-forum, .bloc-header-form {
    color: #fbefa7;
}

.panel-jv-forum .titre-info-fofo {
	color : #f5e480;
}

.bloc-message-forum {
    color: #eeeeee;
    border: 1px solid #444444;
}

.bloc-message-forum .bloc-header {
    border-bottom: 1px dotted #666666;
}

.bloc-message-forum .bloc-pseudo-msg {
    color: #eeeeee;
}

.bloc-message-forum .info-edition-msg {
	color: #eeeeee;
}

.btn-actu-new-list-forum, .btn-topic-resolu, .btn-lock-topic, .btn-repondre-msg {
    background-color: #494949;
    border: 1px solid #444444;
}

.btn-actu-new-list-forum:hover, .btn-topic-resolu:hover, .btn-lock-topic:hover {
  	background-color: #555555;
}

.panel-jv-forum {
    background-color: #494949;
}

#table-liste-topic-forum thead, .panel-jv-forum .panel-heading {
    background-color: #272727;
}

ul.liste-default-jv, ol.liste-default-jv {
	color : #fff;
}

.bloc-liste-num-page span
{
	color : #fff;
}

.bloc-liste-num-page span span
{
	color : #fbefa7 !important;
}

.bloc-liste-num-page .page-active {
	background-color : #fbefa7;
	color : #383838;
}

.jv-editor .conteneur-editor {
    background-color: #494949;
    border: 1px solid #444444;
}

.jv-editor .btn-group {
    background-color: #404040;
    border: 1px solid #444444;
}

.jv-editor .btn-jv-editor-toolbar {
    color: #eeeeee;
}

.jv-editor .btn-jv-editor-toolbar:hover {
	color: #ff4000;
}

.jv-editor .label-previsu {
    color: #bbbbbb;
}

.jv-editor .btn-jv-editor-previsu {
    background-color: #666666;
    border: 1px solid #444444;
}

.jv-editor .btn-jv-editor-previsu.active {
    background-color: #555555;
    border: 1px solid #444444;
}

.jv-editor .area-editor {
    background-color: #666666;
    color: #eeeeee;
}

.jv-editor .text-editor {
    background-color: #5f5f5f;
  	border-top: 1px solid #444444;
}

.jv-editor .previsu-editor {
  	color: #eeeeee;
    border: 1px solid #444444;
}

input[type=text], select, .btn-lancer-rech, .btn-ajouter-sondage {
  	background-color: #666666 !important;
  	color: #eeeeee !important;
  	border: 1px solid #444444 !important;
}

.btn-poster-msg {
	color : #464646;
	background-color: #f5e480;
  	width: 180px;
}

.btn-poster-msg:hover {
  	color: #383838;
	background-color: #fbefa7;
}
    
.btn-poster-msg:hover:before {
  	content: ">> ";
}
        
.btn-poster-msg:hover:after {
  	content: " <<";
}

.icon_search {
  	
}

.signature-msg {
    border-top: 1px dotted #666666;
    color: #999999;
}

.bloc-message-forum .signature-msg
{
	color : #ccc;
}

.conteneur-messages-pagi {
    border-bottom: 1px solid #444444;
}

.bloc-spoil-jv .txt-spoil {
	background : #600;
}

.contenu-spoil, .contenu-spoil a {
	color : #000;
}
}

/* MP */

@-moz-document url-prefix("http://www.jeuxvideo.com/messages-prives/") {
#content {
    background-color: #4d4d4d;
    background-image: none;
}

.container-content, .list-msg {
    background-color: #5f5f5f;
}

.bloc-fil-ariane-crumb .fil-ariane-crumb a, .bloc-fil-ariane-crumb-forum .fil-ariane-crumb a {
	color : #f5e480;
	font-weight : bold;
	font-size : 1.1em;
}

.bloc-fil-ariane-crumb .fil-ariane-crumb a:hover, .bloc-fil-ariane-crumb-forum .fil-ariane-crumb a:hover {
	color : #739dfe;
}

.bloc-fil-ariane-crumb .fil-ariane-crumb .highlight, .bloc-fil-ariane-crumb-forum .fil-ariane-crumb .highlight {
	color : #c9ac00;
	font-weight : bold;
	font-size : 1.1em;
}

/* Menu de gauche */

#mp-menus {
	background-color: #494949;
}

#mp-menus .nouveau-msg a {
	background-color: #272727;
}

#mp-menus .dossiers {
	background-color: #272727;
}

#mp-menus .dossier a {
	color: #f5e480;
}

#mp-menus .dossier  .nonlus a {
	font-weight: bold;
}

#mp-menus .dossier:hover a, #mp-menus .active  a{
	color: #f00;
}

#mp-menus .btn-only-open{
	color: #f5e480;
}

#mp-menus .btn-only-open:hover{
	color: #739dfe;
}

#mp-menus .btn-only-open.open {
	color: #fbefa7;
}

#mp-menus input, #mp-menus select {
	background-color: #666666 !important;
	color: #eeeeee !important;
	border: 1px solid #444444 !important;
}

#mp-menus .btn-valid-mp, #mp-menus .btn-cancel-mp {
	background-color: #f5e480;
	color: #464646;
}

/* Boîtes de réception */

.mp-pagination .nb-pages b {
	color: #fbefa7;
}

.list-msg {
	border: 1px solid #444444;
}

.list-msg th {
	background-color: #272727;
	border: none;
	text-transform: uppercase;
}

.list-msg tbody .dropdown, .list-msg .sujet-msg a, .list-msg tbody .date-msg {
	color: #fff;
}

.list-msg tbody  .lu .dropdown, .list-msg .lu .sujet-msg a, .list-msg .lu .date-msg {
	color : #bbb;
}

.list-msg .sujet-msg a:hover {
	color: #739dfe;
}

.list-msg tbody tr:nth-child(even) {
    background-color: #404040;
}

.list-msg tbody tr:nth-child(odd) {
    background-color: #494949;
}

.list-msg  tbody .list-auteurs {
	background-color: #494949;
}

.list-msg tbody .dropdown .text-admin {
	color: #e94242;
	font-weight: bold;
}

.list-msg tbody .dropdown .text-redac {
	color: #28aa17;
	font-weight: bold;
}

/* Nouveau message */

#nouveau-msg {
	color: #fff;
}

#nouveau-msg .titre-head-bloc .titre-bloc {
	color: #fbefa7;
}

#nouveau-msg .form-control-tag-inner {
	background-color: #272727;
}

#nouveau-msg input {
	background-color: #666666 !important;
	color: #eeeeee !important;
	border: 1px solid #444444 !important;
}

.jv-editor .conteneur-editor {
    background-color: #494949;
    border: 1px solid #444444;
}

.jv-editor .btn-group {
    background-color: #404040;
    border: 1px solid #444444;
}

.jv-editor .btn-jv-editor-toolbar {
    color: #eeeeee;
}

.jv-editor .btn-jv-editor-toolbar:hover {
	color: #ff4000;
}

.jv-editor .pull-right {
    color: #bbbbbb;
	text-transform: uppercase;
	font-family: "robotoboldcondensed";
	font-size: 12px;
	vertical-align: text-bottom;
	padding-right: 8px;
}

.jv-editor .btn-jv-editor-previsu {
    background-color: #666666;
    border: 1px solid #444444;
}

.jv-editor .btn-jv-editor-previsu.active {
    background-color: #555555;
    border: 1px solid #444444;
}

.jv-editor .area-editor {
    background-color: #666666;
    color: #eeeeee;
}

.jv-editor .text-editor {
    background-color: #5f5f5f;
  	border-top: 1px solid #444444;
}

.jv-editor .previsu-editor {
  	color: #eeeeee;
    border: 1px solid #444444;
}

/* Affichage d'une discussion */

#discussion h1, #discussion .titre-discut {
	background-color: #272727;
}

#destinataires {
	color: #fff;
}

#destinataires .form-control-tag {
	background-color: #494949;
	border: 1px solid #444444;
}

#destinataires .form-control-tag-inner .text-redac {
	color : #28aa17;
}

#destinataires .form-control-tag .label {
	background-color: #fbefa7;
}

#destinataires .label a {
	color: #444444;
}

#destinataires .label a.text-admin {
	color : #e94242;
}

#destinataires .label a.text-redac {
	color : #28aa17;
}

#destinataires .label a :hover{
	color: #739dfe;
}

a.bloc-pseudo-msg.text-admin {
	color: #e94242;
}

a.bloc-pseudo-msg.text-redac {
	color: #28aa17;
}

a, .lien-jv, #discussion .bloc-date-msg {
    color: #f5e480;
}

#repondre-mp .bloc-header-form {
	color: #fbefa7;
}

a:hover, .lien-jv:hover {
    color: #739dfe;
}

.bloc-message-forum:nth-child(even) {
    background-color: #404040;
}

.bloc-message-forum:nth-child(odd) {
    background-color: #494949;
}

.bloc-message-forum {
    color: #eeeeee;
    border: 1px solid #444444;
}

.bloc-message-forum .bloc-header {
    border-bottom: 1px dotted #666666;
}

.bloc-message-forum .bloc-pseudo-msg {
    color: #eeeeee;
}

.btn-poster-msg {
	color : #464646;
	background-color: #f5e480;
  	width: 180px;
}

.btn-poster-msg:hover {
  	color: #383838;
	background-color: #fbefa7;
}
    
.btn-poster-msg:hover:before {
  	content: ">> ";
}
        
.btn-poster-msg:hover:after {
  	content: " <<";
}

}
