style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cronometer.com") {  
  @media all and (min-width: 800px) and (max-width: 1234px) {
    body, #nutrimeterApp { min-width: initial !important }
    #content, .GPBYFDEKM { width: auto !important }
    
    #main-header .main-nav { margin-left: 26px !important }
    .GHIM42QCII { margin-right: 5px !important }
    
    .GHIM42QCEP {
      position: absolute;
      left: -225px;
      z-index: 999;
      
      transition: all 0.25s ease-in-out;
    }
    
    .GHIM42QCEP:hover { left: 0 }
  }
}
