style.css:
@-moz-document domain("themeforest.net") {

html body {background: #f3f3f3;}

html.js { font-size: 14px; line-height: 1.4; color: #222; }

html .page-info { background: #f2f2f2; }

html .page-info, html .page-tabs .selected a, html .page-tabs--elite-author .selected a { border-bottom: 0; }

html .content-main, html .header-categories, html .page-tabs .selected a, html .page-tabs--elite-author .selected a, html .page-tabs a:hover, html .page-tabs--elite-author a:hover, html .page-tabs a:focus, html .page-tabs--elite-author a:focus, html .thread__meta>span { background-color: #e7e7e7; }

html .header-sites a.is-active:after { border-bottom-color: #e7e7e7; }

html .box, html .box--bottom-section { border: 0; }

html .header-categories { border-bottom: 0; height: 46px; }

html .header-categories__links { padding: 6px 0; }

html .user-post__post, html .user-post__post--item-note { background-color: #f6f6f6; }

html .thread__title a { font-size: 22px; }

html .page-info h1 { font-size: 25px; font-weight: 500; margin-top: 6px; }

html #thread_reply_content { border-color: #ddd; }

html .page-info .breadcrumbs { display: none; }

html span.elite-indicator.badge-elite_author {
    display: none;
}

html .notice {
    background: #ddd;
}

html .community-badges__badge--s, html .community-badges__badge-wrapper--s { width: 24px; height: 24px; }

html .post-avatar__badges { margin-top: 5px; width: auto; }

html .badges li { margin: 1px; }

html .existing-value{ border:1px solid #ddd !important; border-bottom:1px solid #ddd !important;}

html .existing-value:hover{ background-color:#fff !important;}

}
