style.css:
@-moz-document regexp("https://.*mail.yahoo.com/neo/.*") {
  /* remove the links on the top */
  div#masterNav { display: none !important; }

  /* make the search bar and options 50% of the size */
  div#uhWrapper { zoom: .5 !important; -moz-transform: scale(.7); }
  div#UH { height: 1.5em !important; }
  div#main { top: 1.55em !important; }
  /* same for recent message tabs */
  .nav-tabs .nav-tab-li a {line-height: 2em !important; }
  .nav-tabs .nav-tab-li { height: 2em !important; }
  #tabs { margin-top: 15px; }
  /* readable font insize zoomed area */
  /* - search butons */
  div#uhWrapper .D-ib button { font-size: 2em !important; }
  /* - user menu and settings */
  div#uhWrapper li { font-size: 1.5em !important; }
  /**/


  /* sane font size */
  body, input, button, select, textarea { font-size: 1em !important; }

  /* tool bar from 36px to 20px ... may remove it entirely since heavy users use the list buttons or keyboard...*/
  #toolbar, #toolbar .btn { line-height: 20px !important; height: 20px !important; }
  #inboxcontainer { top: 20px !important; }
  .message { top: -17px !important; }

  /* deleted email tooltip/undo box */
  .yui3-overlay.notification-overlay { top: 22px !important; }

  /* smaller date lines */
  .list-view-item-date-label { line-height: initial !important; height: initial !important; font-size: 8.5px; }
}
