style.css:
@-moz-document url-prefix("https://mail.google.com/mail") {
table{
  counter-reset: num;
}

tr[aria-labelledby]{
  counter-increment: num;
}

tr[aria-labelledby] > td:nth-child(2) > *:first-child:before{
  display: block;
  float: left;
  width: 12px;
  text-align: center;
  content: counter(num);
}
}
