style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("aliexpress.com") {

html, body, #header, .product-info-operation,
.bp-horizontal-banner, .item, .content-bottom,
.foot, #search-key
{
background:#000 !important;
}
.cate-name, .sub-cate, .narrow-down-area,
.view-filter, .ui-textfield, .sel-free,
.ui-textfield-system, .sel-rated, .sel-BulkQty, .sale-items, .sel-online, .item-hover, .ui-autocomplete-ctn,
.link-fake-selector, .list-title, .list-container, #form-search
{
background:#333 !important;
}
a, p, h1, h2, h3, h4, h5, li, .num, .cate-title, .clearfix span, .free-s, .sub-info, .shipping-cost, .narrow-down-area, .a-sale-items,
.total-price, em, dt, .s-serve, .sp-1, .notranslate, .ui-textfield, .title, .brand-info, #search-key, .ui-textfield-system, .atm16, .label,
.bulkQty-a
{
color:#3366cc !important;
}
a:hover {
text-decoration:underline !important;
}
.odd, .even {
border:none !important;
}
}
