style.css:
@-moz-document domain('chan.sankakucomplex.com'), url(https://chan.sankakucomplexxx.com/) {

	/************************************************************************/
	/*	Sankaku Channel SC v2.7.2											*/
	/*	https://chan.sankakucomplex.com/forum/show/2503						*/
	/*	https://userstyles.org/styles/108798/sankaku-channel-sc-dark-light	*/
	/*																		*/
	/************************************************************************/
	/*	To fix the notes, download this userscript:							*/
	/*	https://sleazyfork.org/en/scripts/13267-sankaku-channel-notefix     */
	/*																		*/
	/************************************************************************/
	
	/************************************/
	/*	SC0 - List						*/
	/*	SC1 - Global					*/
	/*	SC2 - News Articals				*/
	/*	SC3 - Notifications				*/
	/*	SC4 - Hide all Ads				*/
	/*	SC5 - Header					*/
	/*	SC6	- Side Bar					*/
	/*	SC7	- Login						*/
	/*	SC8	- My Account				*/
	/*	SC9	- My Profile				*/
	/*	SC10 - Post List				*/
	/*	SC11 - Comments & Forum			*/
	/*	SC12 - Image View Page			*/
	/*	SC13 - Rankings					*/
	/*	SC14 - Mail						*/
	/*	SC15 - Addons					*/
	/*	SC16 - Global Table Styling		*/
	/*	SC17 - Common Box Styling		*/
	/*	SC18 - Text Colors				*/
	/*	SC19 - Links					*/
	/*									*/
	/************************************/
	

	/* SC1 Global */
	body, body.en, body.ja {
		background-color: var(--theme-bga) !important;
	}

	#content {
		padding: 0 0 300px !important;
		margin: 15px auto 0 !important;
		max-width: 1200px;
	}

	#share, cloudflare-app {
		display: none !important;
	}

	#artist-update,
	#right-col #edit,
	#right-col #comments {
		float: none !important;
		width: 750px !important;
		margin: 0 auto !important;
	}

	.blacklisted {
		filter: blur(4px) grayscale(50%);
		-webkit-filter: blur(4px) grayscale(50%);
		transition: 100ms filter linear, 
								100ms -webkit-filter linear;
	}

	.blacklisted:hover {
			/* Reveal blurred thumbnails when hovering */
		filter: unset !important;
		-webkit-filter: unset !important;
	}
	
	@media screen and (max-width: 1500px) {
		#post-list .content {
				max-width: 1020px !important;
				margin-left: 90px !important;
	}
	}

	.status-notice {
		max-width: 850px !important;
	}

	#post-list .sidebar {
		transform: translateX(-50px);
		transition: transform 300ms;
		transition-delay: 500ms;
	}

	#post-list .sidebar:hover {
		transform: translateX(0px);
		transition: transform 150ms;
	}


	/*--------------------------------------------------------*/
	/*--------------------------------------------------------*/

	/*** Style Settings ***/
	
	:root {
		/* Theme */
		/*[[theme]]*/
		/* Link Colors */
		/*[[links]]*/
	}
	
		/*[[linkBold]]*/
		
		/* Sidebar */
		
		/*[[sbscroll]]*/
	
	/*--------------------------------------------------------*/
	/*--------------------------------------------------------*/

	/* SC2 News Articals */
	#headerthumbs {
			/* Articals box positioning */
		padding-top: 2em !important;
		padding-bottom: 27px !important;
		margin-left: -302px !important;
		height: 150px !important;
		width: 635px !important;
		left: 50% !important;
		position: absolute;
			/* Animation */
		box-shadow: 0 8px 7px -8px rgba(0, 0, 0, 0.6);
		transform: translateY(-188px);
		transition: transform 400ms, box-shadow 400ms;
		transition-delay: 500ms;
		z-index: 10001 !important;
			/* Styling */
		background: var(--theme-bg) !important;
		border-width: 0 2px 2px !important;
		border-style: solid !important;
		border-color: var(--theme-br) !important;
		}

	#headerthumbs:hover {
		box-shadow: 0 10px 9px -10px rgba(0, 0, 0, 0.8);
		transform: translateY(-2px);
		transition: transform 400ms, box-shadow 400ms;
		z-index: 10001 !important;
		}

	#headerthumbs ul {
			/* Thumbnail container */
		padding-left: 1.25em !important;
		margin: 0 auto !important;
		width: 635px;
		}

	#headerthumbs ul li {
			/* Makes sure the news aticals don't hit the edge of the card */
		padding-left: 0.5em !important;
		padding-right: 0.5em !important;
		max-height: 164px;
		overflow-y: hidden;
		}

	/*--------------------------------------------------------*/
	/*--------------------------------------------------------*/

	/* SC3 Notifications */
	#news-ticker {
			/* Move news notifications to the bottom */
		position: fixed;
		bottom: 15px;
		right: 15px;
		max-width: 300px !important;
		z-index: 1000;
			/* Styling */
		background: var(--theme-bg) !important;
		border: 2px solid var(--theme-br) !important;
		box-shadow: 5px 8px 7px -8px rgba(0, 0, 0, 0.6);
	}

	#notice {
			/* Red text on top */
		position: fixed !important;
		padding: 5px 10px !important;
		width: auto !important;
		top: 10px !important;
		left: 10px !important;
			/* Styling */
		color: #F00 !important;
		font-size: 14pt !important;
		box-shadow: 0px 0px 8px rgba(0, 0, 0, 0.7);
	}

	/*--------------------------------------------------------*/
	/*--------------------------------------------------------*/

	/* SC4 Hide all Ads */
	.scad,
	.scad-i,
	.sidebar iframe,
	#sank-prestitial {
		display: none !important;
	}

	/*--------------------------------------------------------*/
	/*--------------------------------------------------------*/

	/* SC5 Header */
	#site-title {
		margin: 0 !important;
		font-size: 16px !important;
	}

	#header {
			/* Space between Header and Content */
		cursor: default;
		margin-bottom: 0 !important;
			/* Styling */
		background: var(--theme-hdr) repeat fixed !important;
	}

	#site-title {
		padding-top: 10.5px !important;
	}

	#navbar>li,
	#subnavbar>li {
		font-size: 16px !important;
	}
	.subnav {
		font-size: 12px !important;
	}


	#navbar {
		min-width: 100vw !important;
	}

	#navbar li {
			/* Current page fix */
		padding: 2px 0px 5px !important;
		border-width: 2px !important;
		border-style: solid !important;
		border-color: transparent !important;
	}

	#navbar li>a {
		padding: 2px 10px 3px !important;
	}

	#navbar li:hover {
		background: var(--theme-bg) !important;
		border: 2px solid var(--theme-br) !important;
		border-bottom: 2px solid var(--theme-bg) !important;
	}

	#navbar li.current-page {
		background: var(--theme-bg) !important;
		border: 2px solid var(--theme-br) !important;
		border-bottom: 1px solid var(--theme-bg) !important;
	}

	#navbar li.lang-select {
		border: 0 solid transparent !important;
	}

	#navbar li>ul {
			/* Hover background fix */
		background: var(--theme-bg) !important;
		height: auto !important;
	}

	#navbar li.lang-select:hover {
			/* Lang- hover fix */
		background: none !important;
		border: 0 solid transparent !important;
	}

	#navbar li>ul>li {
			/* Hover elements fix */
		background: none !important;
		border: 0 solid #FFF !important;
		margin: 0px 12px !important;
	}

	#navbar li>ul>li:hover {
		background: transparent !important;
		border: 0 solid transparent !important;
	}

	#navbar li>ul>li>a {
		padding: 0px 0px 10px !important;
	}

	.subnav {
		height: auto;
	}

	#subnavbar {
			/* Sub navbar spacing top */
		border-left: 0 !important;
		height: auto !important;
		padding: 0px 20px !important;
		margin-bottom: 0 !important;
		background: var(--theme-bg) !important;
		border-width: 2px 0 !important;
		border-style: solid !important;
		border-color: var(--theme-br) !important;
	}

	#subnavbar li a {
		display: inline-block;
		padding: 5px 0px 7px	!important;
	}

	#headerlogo>a>img,
	#header>div>a {
			/* Hide Sankaku logo */
		display: none !important;
	}

	/*--------------------------------------------------------*/
	/*--------------------------------------------------------*/

	/* SC6 Side Bar */
	#sticky,
	#post-view .sidebar {
		position: absolute;
		top: 118px;
		left: 0px !important;
	}

	#sticky {
		width: 0px !important;
	}

	#sticky.stick {
			/* Fix no-click container */
		width: 0px !important;
		top: 0px;
		overflow: visible !important;
	}

	#post-view .sidebar {
		height: auto !important;
		padding-right: 10px !important;
	}

	.sidebar {
		background-color: var(--theme-bg);
		padding-right: 10px !important;
		margin-right: 0 !important;
	}

	.sidebar #user-excerpt {
		padding-right: 1.2em !important;
	}
	
	#search-form {
		margin: 2px 0px 12px;
	}
	
	#search-form>h5 {
		text-align: center;
		padding: 2px 0px;
	}
	
	.sidebar h5 {
		font-size: 12pt;
	}
	.sidebar>h5:first-of-type {
		text-align: center;
	}

	#wiki-show .sidebar {
		position: sticky;
		width: 184px !important;
		top: 15px;
	}
	
	input#tags {
			/* Text box Styling */
		background-color: var(--theme-bg) !important;
		border-radius: 0;
		height: 25px;
		font-size: 12pt;
		font-weight: 500;
		margin: 0.1em 0 5px -5px !important;
		padding: 2px 5px !important;
		width: 175px !important;
		overflow: visible !important;
		transition: box-shadow 300ms;
	}

	#ad_s_e_all #advanced_search {
		background: none !important;
		border: 1px solid var(--theme-br) !important;
		color: var(--themem-txt) !important;
		margin: 0 !important;
	}
	#ad_s_e_all #options {
		background-color: var(--theme-bg);
	}

	#advanced_search span,
	#advanced_search select,
	#advanced_search input {
		color: var(--themem-txt) !important;
	}

	#advanced_search fieldset {
		width: 178px !important;
		padding: 6px 0px 6px 6px !important;
		margin: 0px !important;
	}

	#suggestions {
		box-shadow: 0 3px 10px 3px rgba(0, 0, 0, 0.5);
	}

	#blacklisted-sidebar {
		display: initial !important;
	}
	
	#rating {
		margin: 0px auto !important;
	}
	#rating .unit-rating,
	#rating .unit-rating li,
	.r1-unit:hover,
	.r2-unit:hover,
	.r3-unit:hover,
	.r4-unit:hover,
	.r5-unit:hover {
			/* Custom stars */
		background-image: var(--rating-stars) !important;
	}

		#rating .unit-rating {
				/* Hollow star positioning */
			background-repeat: repeat-x !important;
			background-attachment: scroll !important;
			background-position: left center !important;
			background-color: transparent !important;
		}

		#rating .unit-rating li {
				/* Filled star positioning */
			background-repeat: repeat !important;
			background-attachment: scroll !important;
			background-position: left top !important;
			background-color: transparent !important;
		}

		.r1-unit:hover,
		.r2-unit:hover,
		.r3-unit:hover,
		.r4-unit:hover,
		.r5-unit:hover {
				/* Hover star positioning */
			background-repeat: repeat !important;
			background-attachment: scroll !important;
			background-position: left bottom !important;
			background-color: transparent !important;
		}

		.favoriteIcon {
				/* Custom favorite heart */
			background-image: var(--favorite-heart) !important;
			margin: 0px auto !important;
		}

		#add-to-favs,
		#remove-from-favs {
			background-image: none !important;
			padding: 10px 0px 0px;
		}
	
	div[itemprop="aggregateRating"] {
		padding-bottom: 5px;
		text-align: center;
	}
	
	#remaining-favs-link {
		display: inline !important;
		white-space: nowrap;
	}

	#tag-sidebar li {
		margin-bottom: 1px !important;
	}

	/* Edit posts */
	body.en[style="background-color: rgb(51, 170, 51);"] .sidebar {
		box-shadow: 0px 0px 0px 12px rgba(51, 170, 51, 1) !important;
	}
	/* Add/Remove favorites */
	body.en[style="background-color: rgb(255, 255, 170);"] .sidebar {
		box-shadow: 0px 0px 0px 12px rgba(255, 255, 170, 1) !important;
	}
	/* Rate Safe */
	body.en[style="background-color: rgb(102, 255, 102);"] .sidebar {
		box-shadow: 0px 0px 0px 12px rgba(102, 255, 102, 1) !important;
	}
	/* Rate Questionable */
	body.en[style="background-color: rgb(170, 170, 170);"] .sidebar {
		box-shadow: 0px 0px 0px 12px rgba(170, 170, 170, 1) !important;
	}
	/* Rate Explicit/Flag post */
	body.en[style="background-color: rgb(255, 102, 102);"] .sidebar {
		box-shadow: 0px 0px 0px 12px rgba(255, 102, 102, 1) !important;
	}
	/* Rate Stars */
	body.en[style="background-color: rgb(170, 255, 170);"] .sidebar {
		box-shadow: 0px 0px 0px 12px rgba(170, 255, 170, 1) !important;
	}
	/* Apply tag script */
	body.en[style="background-color: rgb(170, 51, 170);"] .sidebar {
		box-shadow: 0px 0px 0px 12px rgba(170, 51, 170, 1) !important;
	}
	/* Apply tag script (Sanakau Addon) */
	body.en[style="background-color: rgb(255, 221, 255);"] .sidebar {
		box-shadow: 0px 0px 0px 12px rgba(255, 221, 255, 1) !important;
	}

	/*--------------------------------------------------------*/
	/*--------------------------------------------------------*/

	/* SC7 Login */
	#user-login {
		margin: 0 auto !important;
	}

	#user-login>form>.form {
		width: 100% !important;
	}

	/*--------------------------------------------------------*/
	/*--------------------------------------------------------*/

	/* SC8 My Account */
	.user-home-heading img {
		margin: 5px 18px 5px 5px !important;
	}

	#user-index {
		padding: 1em !important;
		margin: 0 auto !important;
		width: 600px !important;
	}

	/*--------------------------------------------------------*/
	/*--------------------------------------------------------*/

	/* SC9 My Profile */
	.user-show-heading {
		margin-bottom: 0 !important;
		text-align: center !important;
	}

	.user-show-heading h2 {
		display: inherit !important;
	}

	.user-show-preview {
		padding: 1em 0 0 !important;
		float: none !important;
		width: 100% !important;
	}

	.user-show-preview h4 {
		margin: 0 auto 5px !important;
		width: 860px !important;
	}

	.user-show-preview div {
		display: table !important;
		margin: 0 auto !important;
	}

	/*--------------------------------------------------------*/
	/*--------------------------------------------------------*/

	/* SC10 Post List */
	#post-list .content {
		position: initial;
		padding-left: 0;
		width: 100%;
	}

	#popular-preview {
		border: 2px solid var(--theme-br) !important;
	}
	
	#post-list .blacklisted {
		display: none !important;
	}
	
	#post-list #popular-preview .blacklisted {
		display: inline-block !important;
	}

	/* Thumbnail Styling */
	img.preview {
		border: none !important;
		box-shadow: 0 6px 5px -5px rgba(0, 0, 0, 0.9);
		padding: 0 !important;
		transition: box-shadow 300ms, transform 300ms;
	}

		img.preview:hover {
			box-shadow: 0 13px 6px -9px rgba(0, 0, 0, 0.6) !important;
			transform: translateY(-5px);
			transition: box-shadow 150ms, transform 150ms;
		}

		img.favorited {
			border: 2px solid #E69 !important;
			box-shadow: 0 0 10px 5px #C69 !important;
			padding: 0 !important;
		}

		img.favorited:hover {
			box-shadow: 0 0 10px 5px #C69 !important;
			padding: 0 !important;
		}

		img.has-parent {
			border: 2px solid #FFD700 !important;
		}

		img.has-children {
			border: 2px solid #6F6 !important;
		}

		img.pending {
			border: 2px solid #4B4BA3 !important;
		}

		img.flagged {
			border: 2px solid #F00 !important;
		}

	/*--------------------------------------------------------*/
	/*--------------------------------------------------------*/

	/* SC11 Comments & Forum Posts */
	#comments .no-comments:after {
		content: " :(";
	}

	.comment {
		padding: 1em 1em 11px !important;
	}

		.comment>.author {
			padding: 5px 5px 2px;
			width: 106px !important;
		}

		.comment .content {
			float: left !important;
			padding-bottom: 20px !important;
			margin-left: 2em !important;
			min-height: 123px;
			width: 570px !important;
		}

		.comment .footer {
			position: absolute;
			left: 0px;
			bottom: 0px;
			margin: 0 !important;
		}

	#comment-list .post {
		margin: 0 0 3em 25px !important;
	}

	#respond-link {
		padding-left: 180px;
		margin: 0 !important;
	}

	@media screen and (min-width: 1200px) {
		#forum .comment {
			margin-left: 62px !important;
		}

		#reply {
			margin-left: 70px;
		}
	}

	#forum {
		display: block !important;
		margin: 0 10px !important;
		width: 1180px !important;
	}

		#forum .comment {
			display: inline-block !important;
			padding: 14px !important;
			width: 1024px !important;
			max-width: 1024px !important;
		}

		#forum .comment>.author {
			width: 158px !important;
		}

		#forum .comment .content {
			min-height: 179px;
			width: 826px !important;
		}

	#preview {
		padding: 5px 8px !important;
		margin-left: 72px !important;
		max-width: 1038px !important;
		overflow-x: scroll;
	}

	#reply {
		margin-left: 70px !important;
	}

	blockquote,
	#comments .preview {
		padding: 5px 8px !important;
		margin: 0 0 1em 1em !important;
	}

		blockquote p {
			margin-bottom: 0 !important;
		}

	.col2 .preview {
		padding: 5px 8px !important;
		margin: 0 0 1em !important;
	}

	.response-list>.comment {
		background: var(--theme-bg) !important;
		border: 2px solid var(--theme-br) !important;
	}

	.response-list>.comment>.author,
	.message-user {
		background: var(--theme-br) !important;
	}

	.response-list>.comment>.content>.footer a {
		font-weight: normal !important;
	}

	blockquote,
	#preview,
	.preview {
		/* Quotes */
		background: rgba(255, 255, 255, 0.05);
		border: 1px solid rgba(255, 255, 255, 0.1);
	}

	/*--------------------------------------------------------*/
	/*--------------------------------------------------------*/

	/* SC12 Image View Page */
	.status-notice {
			/* Notice parents,childs,related */
		margin: 0 auto 1em !important;
		padding: 0 !important;
		height: auto !important;
		max-width: 960px !important;
	}

	.status-notice:hover #parent-preview,
	.status-notice:hover #child-preview {
			/* Reveal parent/child thumbnail when hovering */
		display: inline-block !important;
	}

	#parent-preview,
	#child-preview {
			/* Removes parent/child thumbnails when not hovering */
		margin: 6px 0 4px !important;
		display: none !important;
	}

	#parent-preview .thumb,
	#child-preview .thumb {
			/* Preview height fix */
		height: auto !important;
	}

	#pending-notice {
		background: var(--theme-bgpe) !important;
		border: 2px solid var(--theme-brpe) !important;
	}

	.status-notice.flagged {
		background: var(--theme-bgfl) !important;
		border: 2px solid var(--theme-brfl) !important;
	}

	#post-view #right-col {
		position: inherit !important;
		padding-left: 0 !important;
		margin: 0px auto 1em !important;
		width: 100% !important;
	}

	#post-content {
		padding-top: 0 !important;
	}

	#post-content #image {
		display: block !important;
		margin: 0 auto !important;
		width: auto;
		max-width: 100vw;
		height: auto;
	}

	#non-image-content {
		margin: 0 !important;
		text-align: center !important;
	}

	#note-container {
		margin-left: 0;
	}

	#note-count {
		text-align: center;
	}

	#post-view #right-col #recommended {
		float: none !important;
		margin: 1em auto 0 !important;
		width: 850px !important;
	}

	#recommendations-paginator > span.recommended-prev,
	#recommendations-paginator > span.recommended-next {
		padding: 0px;
	}
	.recommended-prev a,
	.recommended-next a {
		padding: 2px 6px;
	}

	#edit-form {
		width: 737px !important;
	}

	#edit-form #post_source,
	#edit-form #post_tags {
		width: calc(100% - 10px) !important;
	}

	#edit-form #post_tags {
		padding: 0.2em;
	}

	/* Rating Radio Styling */
	input#post_rating_explicit,
	input#post_rating_questionable,
	input#post_rating_safe {
		display: none;
	}

		label[for="post_rating_explicit"] {
			cursor: pointer;
			margin-left: 22px;
		}

		label[for="post_rating_questionable"],
		label[for="post_rating_safe"] {
			cursor: pointer;
			margin-left: 27px;
		}

		label[for="post_rating_explicit"]:before,
		label[for="post_rating_questionable"]:before,
		label[for="post_rating_safe"]:before {
			content: "";
			display: inline-block;
			width: 15px;
			height: 15px;
			margin: 0 0 0 -21px;
			position: absolute;
			border-radius: 10px;
			border: 1px solid var(--theme-br);
			background-color: var(--theme-bg);
		}

		input#post_rating_explicit:checked+label[for="post_rating_explicit"]:before,
		input#post_rating_questionable:checked+label[for="post_rating_questionable"]:before,
		input#post_rating_safe:checked+label[for="post_rating_safe"]:before {
			content: "\2022";
			color: var(--theme-txt);
			font-size: 12pt;
			text-align: center;
			line-height: 15px;
		}

		th label[for="post_rating_questionable"]:before {
			display: none;
		}

	/*--------------------------------------------------------*/
	/*--------------------------------------------------------*/

	/* SC13 Rankings */
	.ranking-list h1{
		margin-bottom: 10px;
	}

	.ranking-top-spot {
		border: none;
	}

	.ranking-item {
