    box-shadow: 0px 0px 0px 12px rgba(170, 51, 170, 1) !important;
  }
  /* Apply tag script (Sanakau Addon) */
  body.en[style="background-color: rgb(255, 221, 255);"] .sidebar {
    box-shadow: 0px 0px 0px 12px rgba(255, 221, 255, 1) !important;
