  #post-list .content {
    position: initial;
    padding-left: 0;
    width: 100%;
  }

  #popular-preview {
    border: 2px solid var(--theme-br) !important;
  }
  
  #post-list .blacklisted {
    display: none !important;
  }
  
  #post-list #popular-preview .blacklisted {
    display: inline-block !important;
