  /*--------------------------------------------------------*/

  /* SC12 Image View Page */
  .status-notice {
    	/* Notice parents,childs,related */
    margin: 0 auto 1em !important;
    padding: 0 !important;
    height: auto !important;
    max-width: 960px !important;
  }

  .status-notice:hover #parent-preview,
  .status-notice:hover #child-preview {
      /* Reveal parent/child thumbnail when hovering */
    display: inline-block !important;
  }

  #parent-preview,
  #child-preview {
    	/* Removes parent/child thumbnails when not hovering */
    margin: 6px 0 4px !important;
    display: none !important;
  }

