  /* Rating Radio Styling */
  input#post_rating_explicit,
  input#post_rating_questionable,
  input#post_rating_safe {
    display: none;
  }

    label[for="post_rating_explicit"] {
      cursor: pointer;
      margin-left: 22px;
    }

    label[for="post_rating_questionable"],
    label[for="post_rating_safe"] {
      cursor: pointer;
      margin-left: 27px;
    }

