  /*     Mail Notification Node     */
  /* Place below #notice in the DOM */ 
  /**********************************/
  /*
    <div class="has-mail" id="has-mail-notice">
    <a href="/dmail/inbox">You have mail</a>
    </div>
  */

  #has-mail-notice {
    border: none;
    background: none;
    font-size: 2em;
    padding: 15px 0;
    margin: 15px 0;
  }

