style.css:
@-moz-document domain("javascriptkit.com"), domain("dynamicdrive.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

selector
{
	text-transform: capitalize!important;
}

.bluetabs li a,
.headers,
.maincontainer,
.shadetabs li a,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

.samplecode, .samplecodeplus
{
    border: 1px solid gray;
}

code
{
	white-space: nowrap!important;
}

li
{
	display: block!important;
	text-align: left!important;
}

.syntaxhighlighter .line
{
    white-space: pre-line!important;
}

.genesis-nav-menu a
{
    display: inline-block!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

[size="5"][color="#0000FF"]
{
	font-size: 2.15rem!important;
}

#jksitesearch #jksitesearch_cat,

.syntaxhighlighter code,
.syntaxhighlighter div,
.syntaxhighlighter table tbody,
.syntaxhighlighter table td,
.syntaxhighlighter table tr,
.syntaxhighlighter table,
.syntaxhighlighter
{
	font-size: 2.05rem!important;
}

.contentlisting .title,
.headers,

b a,
form.convertcase textarea,
h3
{
	font-size: 1.95rem!important;
}

#toprightdiv,

.examplecode,
	
p,
textarea
{
	font-size: 1.85rem!important;
}

#contentcolumn,

.contentareas li a,
.contentlisting,

body,
table
{
	font-size: 1.5rem!important;
}

selector
{
	font-size: 15px!important;
}

b a
{
	line-height: 55px!important;
}

#middlecolumn table td,
	
.contentlisting,
.examplecode,
	
p
{
	line-height: 39px!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

#rightcolumn,
#topbanner,
	
.adsbygoogle,
.bsacontainer.nomargin,
	
[id*="aswift_"]
	{
	display: none!important;
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

selector
{
    top: 10px!important;
}

/* Margins 
_____________________________________________________________________________________________________*/

#middlecolumn,
	
.clsRef,
.contentareas,
.shadetabs li,
.site-inner,
.wrap
{
	margin: 1.2rem!important;
}

#contentcolumn,
#middlecolumn
{
	margin: 0 0 3rem 21rem!important;
}

#leftcolumn {
	margin-left: -97%;
}

/* Paddings 
_____________________________________________________________________________________________________*/

#contentcolumn,
#leftcolumn,
#middlecolumn,
	
.clsRef,
.content,
.contentareas,
.screenshots,
	
form.convertcase textarea
{
	padding: 1.2rem!important;
}

#toprightdiv #topformbutton,

.contentlisting
{
	padding: 3px 16px!important;
}

.genesis-nav-menu a {
    padding: 5px 10px;
}

/* Heights 
_____________________________________________________________________________________________________*/

selector
{
	max-height: none!important;
}

textarea[rows]
{
	height: auto!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.site-inner,
.wrap
{
	max-width: none!important;
}

#contentwrapper,
#footermenu ul,

.maincontainer
{
	width: 98%!important;
}

.contentareas
{
	width: 97%!important;
}

#middlecolumn {
    width: 87%!important;
}

.content
{
	width: 79%!important;
}

#leftcolumn
{
	width: 11%;
}

}

