style.css:
@-moz-document url-prefix("http://www.roblox.com/"), url-prefix("http://de.roblox.com/"), url-prefix("https://de.roblox.com/") {
#nav-home, #nav-profile, #nav-message, #nav-friends, #nav-character, #nav-inventory, #nav-trade, #nav-group, #nav-forum, #nav-blog {
	color: #990000;
}

.mCSB_container {
	background-color: black;
}

#navigation {
	background-color: black;
}

.rbx-divider {
	background-color: #000000;
}

#navigation > ul > li.rbx-lead:before {
	content: "Luciferic Luci";
  	visibility: visible
}

#navigation > ul > li.rbx-lead:after {
	content: "Luciferic Tools";
  	visibility: visible
}

.rbx-lead {
	color: #990000;
	visibility: hidden;
}

.col-xs-12 > .home-header-content:after {
	content: "Welcome back to ROBLOX, Luciferic Luci!";
	font-style: sans-serif;
	font-size: 38px;
	color: #990000;
	
	visibility: visible;
}

.home-header-content {
	visibility: hidden;
}

#upgrade-now-button {
	color: #990000
}

.rbx-text-notes {
	color: #990000;
}

.hlist {
	background-color: black;
}

.container-main {
	background-color: black;
}

.content {
	background-color: black;
}

.vlist {
	color: #990000;
	background-color: black;
}

#shareButton {
	background-color: black;
	color: #990000;
}

.section {
	background-color: black;
}
#txtStatusMessage {
	background-color: black;
	color: #990000;
}

.section-header {
	color: #990000;
}

.container-footer {
	background-color: black;
}

.container-header {
	color: #990000;
	background-color: black;
}

.home-friends {
	color: red;
	background-color: black;
}

.blog-news {
	visibility: hidden;
}

.section-header {
	visibility: hidden;
}

.section::before {
	content: "Luciferic Luci!";
	color: #990000;
	font-size: 45px;
	visibility: visible;
}
}
